import React, { useState, useEffect } from 'react';
import { Button, Form, Col, Row } from 'react-bootstrap';
import styles from "./ChooseTheme.module.css"
import styles_base from "../../Forms/FormsBase.module.css"
import { useLocation } from 'react-router-dom';

const ChooseTheme = ({ themes_list }) => {
  const [selectedOption, setSelectedOption] = useState('');
  const [url, setUrl] = useState('');

  useEffect(() => {
    const location = window.location.pathname; // Получаем путь из URL
    const teacherName = decodeURIComponent(location.substring(location.lastIndexOf('/') + 1)); // Извлекаем имя руководителя из URL
    setUrl(teacherName);
  }, []);

  const handleSelectChange = (option) => {
    setSelectedOption(option);
  };

  const filteredDepartmentThemes = themes_list.filter(theme => theme.department);
  const urlParts = teacherName.split('%20');
  const lastName = urlParts[0];
  const firstName = urlParts[1];
  const patronymic = urlParts[2];

  const filteredTeacherThemes = themes_list.filter(theme => {
    const teacher = theme.teacher.user;
    return teacher.last_name === lastName && teacher.first_name === firstName && teacher.patronymic === patronymic;
  });

  const studentTheme = themes_list.find(theme => theme.by_student);

  return (
    <div>
      <div className={`${styles.button_group} mb-4`} >
        <Button variant="none" className={styles.button1} onClick={() => handleSelectChange('select1')}>
          Предложить свою тему
        </Button>
        <Button variant="none" className={styles.button2} onClick={() => handleSelectChange('select2')}>
          Выбрать из перечня тем кафедры
        </Button>
        <Button variant="none" className={styles.button3} onClick={() => handleSelectChange('select3')}>
          Выбрать из тем, предложенных руководителем
        </Button>
      </div>
      <div className="select-group my-4">
        <div id="select1" style={{ display: selectedOption === 'select1' ? 'block' : 'none' }}>
          <Form>
            <Form.Group as={Row} controlId="formPlaintextEmail">
              <Form.Label column sm="4">
                Своя тема ВКР
              </Form.Label>
              <Col sm="8">
                <Form.Control type="text" className={styles_base.form_control} placeholder="тема ВКР" />
              </Col>
            </Form.Group>
          </Form>
        </div>
        <div id="select2" style={{ display: selectedOption === 'select2' ? 'block' : 'none' }}>
          <Form>
            <Form.Group as={Row}>
              <Form.Label htmlFor="list_themes_department" column sm={4}>Перечень тем кафедры</Form.Label>
              <Col sm={8}>
                <Form.Control as="select" className={styles_base.form_control} id="list_themes_department">
                  <option value={0}>-</option>
                  {filteredDepartmentThemes.map(theme => (
                    <option key={theme.id} value={theme.id}>{theme.name}</option>
                  ))}
                </Form.Control>
              </Col>
            </Form.Group>
          </Form>
        </div>
        <div id="select3" style={{ display: selectedOption === 'select3' ? 'block' : 'none' }}>
          <Form>
            <Form.Group as={Row}>
              <Form.Label htmlFor="list_themes_teacher" column sm={4}>Перечень тем руководителя</Form.Label>
              <Col sm={8}>
                <Form.Control as="select" className={styles_base.form_control} id="list_themes_teacher">
                  <option value={0}>-</option>
                  {filteredTeacherThemes.map(theme => (
                    <option key={theme.id} value={theme.id}>{theme.name}</option>
                  ))}
                </Form.Control>
              </Col>
            </Form.Group>
          </Form>
        </div>
      </div>
    </div>
  );
};

export default ChooseTheme;
