import React from 'react';
import { Table } from 'react-bootstrap';

function ViewProtectionScheduleTable({ protection_schedule }) {
  // Helper function to convert date format
  const formatDate = (dateString) => {
    const months = [
      'января', 'февраля', 'марта', 'апреля', 'мая', 'июня',
      'июля', 'августа', 'сентября', 'октября', 'ноября', 'декабря'
    ];
    const day = dateString.slice(0, 2);
    const month = months[parseInt(dateString.slice(2, 4), 10) - 1];
    const year = dateString.slice(4, 8);
    return `${day} ${month} ${year}`;
  };

  const generateHeading = (item) => {
    if (item.group) {
      const { name, speciality } = item.group;
      if (name.includes('Б')) {
        return `Бакалавры по специальности ${speciality ? speciality.code : ''} "${speciality ? speciality.name : ''}" по программе ${speciality && speciality.field_of_study ? speciality.field_of_study.code : ''} "${speciality && speciality.field_of_study ? speciality.field_of_study.name : ''}"`;
      } else if (name.includes('М')) {
        return `Магистры по специальности ${speciality ? speciality.code : ''} "${speciality ? speciality.name : ''}" по программе ${speciality && speciality.field_of_study ? speciality.field_of_study.code : ''} "${speciality && speciality.field_of_study ? speciality.field_of_study.name : ''}"`;
      } else {
        return `Группа ${name}`;
      }
    } else {
      return item.additional_information || '';
    }
  };

  // Group by group name
  const groupedByGroupName = protection_schedule.reduce((acc, item) => {
    const groupName = item.group ? item.group.name : null;
    if (!acc[groupName]) {
      acc[groupName] = [];
    }
    acc[groupName].push(item);
    return acc;
  }, {});

  // Group by date
  const groupedByDate = protection_schedule.reduce((acc, item) => {
    const date = item.date;
    if (!acc[date]) {
      acc[date] = [];
    }
    acc[date].push(item);
    return acc;
  }, {});

  const sortedDates = Object.keys(groupedByDate).sort();

  const processedGroups = new Set();

  return (
    <>
      <h4 className="mb-4 text-center">График защит ВКР</h4>
      <div className='mx-5'>
        {sortedDates.map((date, index) => {
          const dateItems = groupedByDate[date];

          // Filter out items that have been processed already
          const unprocessedItems = dateItems.filter(item => !processedGroups.has(item.group ? item.group.name : null));

          if (unprocessedItems.length > 1) {
            const uniqueGroupNames = [...new Set(unprocessedItems.map(item => item.group ? item.group.name : null))];
            uniqueGroupNames.forEach(groupName => processedGroups.add(groupName));

            const sortedGroupNames = uniqueGroupNames.sort((a, b) => {
              if (a && a.includes('М') && b && b.includes('Б')) return -1;
              if (a && a.includes('Б') && b && b.includes('М')) return 1;
              return 0;
            });

            return (
              <div key={index} style={{ marginBottom: '20px' }}>
                {sortedGroupNames.map((groupName, groupIdx) => {
                  const groupItem = unprocessedItems.find(item => item.group && item.group.name === groupName);
                  const heading = generateHeading(groupItem);
                  return (
                    <div key={groupIdx}>
                      <h6>{heading}</h6>
                      <Table bordered style={{ width: '100%' }}>
                        <caption>{heading}</caption>
                        <thead>
                          <tr>
                            <th>ПП</th>
                            <th>Дата</th>
                            <th>Время</th>
                            <th>Аудитория</th>
                            <th>Уч. группа</th>
                          </tr>
                        </thead>
                        <tbody>
                          {unprocessedItems.map((item, idx) => (
                            <tr key={item.id}>
                              <td>{idx + 1}</td>
                              <td>{formatDate(item.date)}</td>
                              <td>9:00</td>
                              <td>{item.audience_number}</td>
                              <td>{item.group ? item.group.name : item.additional_information}</td>
                            </tr>
                          ))}
                        </tbody>
                      </Table>
                    </div>
                  );
                })}
              </div>
            );
          } else {
            return null;
          }
        })}

        {/* Additional Information Table */}
        {groupedByGroupName[null] && (
          <div style={{ marginBottom: '20px' }}>
            <h6>Дополнительная информация</h6>
            <Table bordered style={{ width: '100%' }}>
              <caption>Дополнительная информация</caption>
              <tbody>
                {groupedByGroupName[null].map((item, idx) => (
                  <tr key={idx}>
                    <td>{idx + 1}</td>
                    <td>{formatDate(item.date)}</td>
                    <td>9:00</td>
                    <td>{item.audience_number}</td>
                    <td>{item.additional_information}</td>
                  </tr>
                ))}
              </tbody>
            </Table>
          </div>
        )}
      </div>
    </>
  );
}

export default ViewProtectionScheduleTable;
