import React, { Component } from 'react'
import Header from '../components/Header/Header.js';
import Background from '../components/Background/Background.js';
import Footer from '../components/Footer/Footer.js';

export default class BasePage extends Component {
    constructor(props) {
        super(props);
        this.state = {
          isAuthenticated: false 
        };
      }
    
  render() {
    return (
        <Background>
        <Header isAuthenticated={isAuthenticated} toggleMenu={this.toggleMenu} isSideMenuOpen={isSideMenuOpen} /> {/* Передаем isSideMenuOpen */}
        <SideMenu toggleMenu={this.toggleMenu} isSideMenuOpen={this.state.isSideMenuOpen} />
        <Footer />
      </Background>
    )
  }
}
