import React from 'react'
import Header from '../components/Header/Header.js';
import Background from '../components/Background/Background.js';
import Footer from '../components/Footer/Footer.js';
import SideMenu from '../components/SideMenu/SideMenu.js';

const BasePage = ({ isAuthenticated, toggleMenu, isSideMenuOpen, children }) => {
  const [darkMode, setDarkMode] = useState(false);
  
  const toggleTheme = () => {
    setDarkMode(prevDarkMode => !prevDarkMode);
  };

    return (
      <Background darkMode={darkMode}>
        <Header isAuthenticated={isAuthenticated} toggleMenu={toggleMenu} isSideMenuOpen={isSideMenuOpen} />
        <SideMenu toggleMenu={toggleMenu} isSideMenuOpen={isSideMenuOpen} />
        {children}
        <Footer />
      </Background>
    );
  };
  export default BasePage;
