import petrovich from 'petrovich';
import RussianNouns from 'russian-nouns-js';
import { saveAs } from 'file-saver';
import PizZip from 'pizzip';
import Docxtemplater from 'docxtemplater';
import word from './3.docx'; 

const generateTask = async ({request_data_task, schedule_stud_prof }) => {

    console.log(request_data_task)

    var petrovich = require('petrovich');
    var person = {
        first: request_data_task.student.user.first_name,
        middle: request_data_task.student.user.patronymic,
        last: request_data_task.student.user.last_name,
       };
    const fio = petrovich(person, 'dative');
    const rne = new RussianNouns.Engine();

    function getInitials(firstName, patronymic) {
        return `${firstName.charAt(0)}.${patronymic.charAt(0)}.`;
      }
      const np_s = getInitials(request_data_task.student.user.first_name, request_data_task.student.user.patronymic);
      const np_t = getInitials(request_data_task.teacher.user.first_name, request_data_task.teacher.user.patronymic);
      const year = new Date().getFullYear()
      const declineWord = (word, gender) => rne.decline({ text: word.split(' ')[0], gender }, 'родительный');
      let place_of_work = request_data_task.teacher.user.place_of_work.replace(request_data_task.teacher.user.place_of_work.split(' ')[0], declineWord(request_data_task.teacher.user.place_of_work, 'женский'));
      
      schedule_stud_prof.forEach(sch => {
        sch = sch.completion_mark ? 'выполнено' : 'не выполнено'
      });


  try {
    const response = await fetch(word);
    if (!response.ok) throw new Error('Ошибка сети');
    const arrayBuffer = await response.arrayBuffer();
    const zip = new PizZip(arrayBuffer);
    const doc = new Docxtemplater(zip);

    const dataToRender = {
        year: year,
      dat_student_last_name: fio.last ,
      student_last_name: request_data_task.student.user.last_name,
      student_first_name: fio.first,
      student_patronymic: fio.middle,
      group_name: request_data_task.student.group.name,
      speciality_code: request_data_task.student.group.speciality.code,
      speciality_name: request_data_task.student.group.speciality.name,
      profile_code: request_data_task.student.group.speciality.field_of_study.code,
      profile_name: request_data_task.student.group.speciality.field_of_study.name,
      theme_name: request_data_task.theme.name,
      np_s: np_s,
      np_t: np_t,
      teacher_last_name: request_data_task.teacher.user.last_name,
      job_title: request_data_task.teacher.user.job_title,
      place_of_work: place_of_work,
      schedule_stud_prof: schedule_stud_prof.map(stage => ({
        stage_name: stage.stage_name,
        start: stage.start,
        end: stage.end,
        result: stage.result,
        completion_mark: stage.completion_mark ? 'выполнено' : 'не выполнено',
    }))
    };

    // Установка данных и рендеринг документа
    doc.setData(dataToRender);
    doc.render();

    // Сохранение документа
    const output = doc.getZip().generate({ type: 'blob' });
    saveAs(output, 'Задание на ВКР.docx');
  } catch (error) {
    console.error('Ошибка загрузки файла:', error);
  }
};

export default generateTask;
