import React, { useState } from 'react';
import { Container, Form, Table, Button } from 'react-bootstrap';

function CreateScheduleWorkFQWForm() {
    const [scheduleRows, setScheduleRows] = useState([
        { stageName: '', start: '', deadline: '', result: '' }
    ]);

    const addRow = () => {
        setScheduleRows([...scheduleRows, { stageName: '', start: '', deadline: '', result: '' }]);
    };

    const deleteRow = (index) => {
        const newRows = [...scheduleRows];
        newRows.splice(index, 1);
        setScheduleRows(newRows);
    };

    const handleInputChange = (e, index) => {
        const { name, value } = e.target;
        const newRows = [...scheduleRows];
        newRows[index][name] = value;
        setScheduleRows(newRows);
    };

    return (
        <Container>
            <h4 className="mb-4 text-center">Создать график работы над ВКР</h4>
            <Form>
                <Table striped bordered>
                    <thead>
                        <tr>
                            <th>Наименование этапа</th>
                            <th>Начало выполнения этапа</th>
                            <th>Конец выполнения этапа</th>
                            <th>Результат выполнения этапов</th>
                            <th></th>
                        </tr>
                    </thead>
                    <tbody>
                        {scheduleRows.map((row, index) => (
                            <tr key={index}>
                                <td>
                                    <Form.Control
                                        type="text"
                                        name="stageName"
                                        value={row.stageName}
                                        onChange={(e) => handleInputChange(e, index)}
                                    />
                                </td>
                                <td>
                                    <Form.Control
                                        type="date"
                                        name="start"
                                        value={row.start}
                                        onChange={(e) => handleInputChange(e, index)}
                                    />
                                </td>
                                <td>
                                    <Form.Control
                                        type="date"
                                        name="deadline"
                                        value={row.deadline}
                                        onChange={(e) => handleInputChange(e, index)}
                                    />
                                </td>
                                <td>
                                    <Form.Control
                                        type="text"
                                        name="result"
                                        value={row.result}
                                        onChange={(e) => handleInputChange(e, index)}
                                    />
                                </td>
                                <td>
                                    {index > 0 && (
                                        <Button variant="danger" onClick={() => deleteRow(index)}>
                                            Удалить
                                        </Button>
                                    )}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </Table>
                <Button variant="primary" onClick={addRow}>Добавить строку</Button>
                <Button variant="success" type="submit">Сохранить</Button>
            </Form>
        </Container>
    );
}

export default CreateScheduleWorkFQWForm;
