import React, { useState } from 'react';
import { Table, Button } from 'react-bootstrap';
import * as XLSX from 'xlsx';
import { saveAs } from 'file-saver';
import styles from '../../../container.module.css';

function SecretaryHugeTable({ info_for_table }) {
  const [tableData, setTableData] = useState(info_for_table.requests);
  const [filters, setFilters] = useState({
    averageScore: '',
    theme: '',
    // Добавьте другие фильтры по необходимости
  });

  const handleInputChange = (e, rowIndex, field) => {
    const newData = [...tableData];
    newData[rowIndex][field] = e.target.value;
    setTableData(newData);
  };

  const handleDoubleClick = (e, rowIndex, field) => {
    const target = e.target;
    if (target.tagName === 'TD') {
      const input = document.createElement('input');
      input.type = 'text';
      input.value = target.innerText;
      input.addEventListener('blur', () => {
        const newData = [...tableData];
        newData[rowIndex][field] = input.value;
        setTableData(newData);
        target.innerText = input.value;
      });
      target.innerText = '';
      target.appendChild(input);
      input.focus();
    }
  };

  const exportToExcel = () => {
    const ws = XLSX.utils.table_to_sheet(document.getElementById('dataTable'));
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, 'Data');
    XLSX.writeFile(wb, 'Data.xlsx');
  };

  const getUniqueValues = (data, field) => {
    const uniqueValues = new Set();
    data.forEach(item => {
      uniqueValues.add(item[field]);
    });
    return Array.from(uniqueValues);
  };

  const applyFilters = () => {
    let filteredData = [...info_for_table.requests];
    if (filters.averageScore !== '') {
      filteredData = filteredData.filter(item => item.student.average_score === filters.averageScore);
    }
    if (filters.theme !== '') {
      filteredData = filteredData.filter(item => item.theme.name === filters.theme);
    }
    // Примените другие фильтры по аналогии
    setTableData(filteredData);
  };

  return (
    <>
      <h4 className="mb-4 text-center">Таблица данных</h4>
      <Button onClick={exportToExcel} className="mb-4">Export to Excel</Button>
      <table id="dataTable" className={`${styles.huge_table} table-custom`}>
        <thead>
          <tr>
            <th rowSpan="2">№ п/п</th>
            <th rowSpan="2">ср.б.</th>
            <th rowSpan="2">№ протокола</th>
            <th rowSpan="2">дата протокола</th>
            <th rowSpan="2">Фамилия, Имя, Отчество</th>
            <th rowSpan="2">Тема диплома</th>
            <th rowSpan="2">Руководитель диплома</th>
            <th rowSpan="2">Консультант</th>
            <th rowSpan="2">Рецензент</th>
            <th rowSpan="2">организация</th>
            <th rowSpan="2">№ зачетки</th>
            <th rowSpan="2">Телефон</th>
            <th rowSpan="2">Email</th>
            <th rowSpan="2">бюджет/платный</th>
            <th rowSpan="2">оценка на предзащите/ преддипломная практика</th>
            <th rowSpan="2">оценка на защите</th>
            <th rowSpan="2">дата рождения</th>
            <th rowSpan="2">год окончания предыдущ образования</th>
            <th rowSpan="2">форма предыдущего образования</th>
            <th rowSpan="2">год поступления</th>
            <th rowSpan="2">особые отметки</th>
            <th colSpan="6">приказы</th>
            <th colSpan="12">отметки о выполнении</th>
            <th rowSpan="2">вид ВКР</th>
            <th rowSpan="2">ВКР по заявке</th>
            <th rowSpan="2">ВКР на англ. языке</th>
            <th rowSpan="2">ВКР рекомендовано</th>
            <th rowSpan="2">наличие спец. условий</th>
          </tr>
          <tr>
            <th>допуск</th>
            <th>тема</th>
            <th>изменение темы</th>
            <th>рецензент</th>
            <th>отчисление</th>
            <th>с какого числа отчисление</th>
            <th>долги</th>
            <th>справка о выполнении уч. плана</th>
            <th>сдана зачетка</th>
            <th>сдана пояснительная записка</th>
            <th>красный диплом</th>
            <th>согласие на публикацию</th>
            <th>отзыв</th>
            <th>рецензия</th>
            <th>Акт о внедрении</th>
            <th>отчет о плагиате</th>
            <th>заявление на последипломный отпуск</th>
            <th>рекомендован к поступлению</th>
          </tr>
        </thead>
        <tbody>
          {tableData.map((request, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td onDoubleClick={(e) => handleDoubleClick(e, index, 'student.average_score')}>{request.student.average_score}</td>
              <td>1</td>
              <td>1</td>
              
              <td onDoubleClick={(e) => handleDoubleClick(e, index, 'student.surname')}>
          {`${request.student.surname} ${request.student.name} ${request.student.patronymic}`}
        </td>
        <td onDoubleClick={(e) => handleDoubleClick(e, index, 'theme.name')}>
          {request.theme.name}
        </td>
        <td onDoubleClick={(e) => handleDoubleClick(e, index, 'teacher.surname')}>
          {`${request.teacher.surname} ${request.teacher.name} ${request.teacher.patronymic}`}
        </td>
        <td onDoubleClick={(e) => handleDoubleClick(e, index, 'consultant.surname')}>
          {`${request.consultant.surname} ${request.consultant.name} ${request.consultant.patronymic}`}
        </td>
        <td onDoubleClick={(e) => handleDoubleClick(e, index, 'theme.reviewer.surname')}>
          {`${request.theme.reviewer.surname} ${request.theme.reviewer.name} ${request.theme.reviewer.patronymic}`}
        </td>
        <td onDoubleClick={(e) => handleDoubleClick(e, index, 'theme.organization.name')}>
          {`${request.theme.organization.name} ${request.theme.organization.last_name_of_responsible} ${request.theme.organization.first_name_of_responsible} ${request.theme.organization.patronymic_name_of_responsible} ${request.theme.organization.job_title_of_resposible} ${request.theme.organization.additional_information}`}
        </td>
        <td onDoubleClick={(e) => handleDoubleClick(e, index, 'student.record_book_number')}>
          {request.student.record_book_number}
        </td>
        <td onDoubleClick={(e) => handleDoubleClick(e, index, 'student.phone')}>
          {request.student.phone}
        </td>
        <td onDoubleClick={(e) => handleDoubleClick(e, index, 'student.email')}>
          {request.student.email}
        </td>
        <td onDoubleClick={(e) => handleDoubleClick(e, index, 'student.basis_of_study')}>
          {request.student.basis_of_study}
        </td>
        <td onDoubleClick={(e) => handleDoubleClick(e, index, 'preprotection_grade')}>
          {request.preprotection_grade}
        </td>
        <td onDoubleClick={(e) => handleDoubleClick(e, index, 'workProtecton.final_grade')}>
          {request.workProtecton.final_grade}
        </td>
        <td onDoubleClick={(e) => handleDoubleClick(e, index, 'student.date_of_birth')}>
          {request.student.date_of_birth}
        </td>
        <td onDoubleClick={(e) => handleDoubleClick(e, index, 'student.year_of_prev_education_completion')}>
          {request.student.year_of_prev_education_completion}
        </td>
        <td onDoubleClick={(e) => handleDoubleClick(e, index, 'student.form_of_prev_education')}>
          {request.student.form_of_prev_education}
        </td>
        <td onDoubleClick={(e) => handleDoubleClick(e, index, 'student.year_of_admission')}>
          {request.student.year_of_admission}
        </td>
        <td onDoubleClick={(e) => handleDoubleClick(e, index, 'student.special_marks')}>
          {request.student.special_marks}
        </td>
        <td>1</td>
        <td>1</td>
        <td>1</td>
        <td>1</td>
        <td>1</td>
        <td>1</td>
        <td onDoubleClick={(e) => handleDoubleClick(e, index, 'student.debts')}>
          {request.student.debts ? 'да' : 'нет'}
        </td>
        <td onDoubleClick={(e) => handleDoubleClick(e, index, 'student.certificate_of_curriculum_completion')}>
          {request.student.certificate_of_curriculum_completion}
        </td>
        <td onDoubleClick={(e) => handleDoubleClick(e, index, 'student.record_book_submitted')}>
          {request.student.record_book_submitted}
        </td>
        <td onDoubleClick={(e) => handleDoubleClick(e, index, 'explanatory_note_submitted')}>
          {request.explanatory_note_submitted ? 'да' : 'нет'}
        </td>
        <td onDoubleClick={(e) => handleDoubleClick(e, index, 'student.diploma_with_honors')}>
          {request.student.diploma_with_honors ? 'да' : 'нет'}
        </td>
        <td onDoubleClick={(e) => handleDoubleClick(e, index, 'student.publication_agree')}>
          {request.student.publication_agree}
        </td>
        <td onDoubleClick={(e) => handleDoubleClick(e, index, 'teacher_review')}>
          {request.teacher_review}
        </td>
        <td onDoubleClick={(e) => handleDoubleClick(e, index, 'review')}>
          {request.review}
        </td>
        <td onDoubleClick={(e) => handleDoubleClick(e, index, 'implementation_act')}>
          {request.implementation_act ? 'да' : 'нет'}
        </td>
        <td onDoubleClick={(e) => handleDoubleClick(e, index, 'plagarism_report')}>
          {request.plagarism_report}
        </td>
        <td onDoubleClick={(e) => handleDoubleClick(e, index, 'student.application_for_postgraduate_leave')}>
          {request.student.application_for_postgraduate_leave}
        </td>
        <td onDoubleClick={(e) => handleDoubleClick(e, index, 'student.recommended_for_admission')}>
          {request.student.recommended_for_admission}
        </td>
        <td onDoubleClick={(e) => handleDoubleClick(e, index, 'type_of_fqw')}>
          {request.type_of_fqw}
        </td>
        <td onDoubleClick={(e) => handleDoubleClick(e, index, 'theme.fqw_by_application')}>
          {request.theme.fqw_by_application}
        </td>
        <td onDoubleClick={(e) => handleDoubleClick(e, index, 'fqw_in_english')}>
          {request.fqw_in_english ? 'да' : 'нет'}
        </td>
        <td onDoubleClick={(e) => handleDoubleClick(e, index, 'fqw_recommended')}>
          {request.fqw_recommended}
        </td>
        <td onDoubleClick={(e) => handleDoubleClick(e, index, 'special_conditions')}>
          {request.special_conditions ? 'да' : 'нет'}
        </td>
</tr>
          ))}
        </tbody>
      </table>
    </>
  );
}

export default SecretaryHugeTable;
