import React, { Component } from 'react';
import BaseModal from '../BaseModal/BaseModal';
import BlockInfo from '../../BlockInfo/BlockInfo';
import styles from './ModalRequests.module.css';
import RequestsContent from '../../ProfileBlocks/Teacher/RequestsCard/RequestsContent';

export default class ModalRequests extends Component {
    
    render() {
      const { show, handleClose, darkMode, requests, noRequestsLeft, requestdata, handleAccept, handleDecline } = this.props;
      const modalstyle = {
        modal: styles.container_modal 
      };
      const content = (
          <div>
            <BlockInfo>
            Вы можете в одностороннем порядке принять или отклонить заявки от выпускников.
            Если Вы принимаете заявку, то можете утвердить тему, поставив галочку в соответствующее окно. Если Вы считаете,
            что тему нужно подкорректировать, оставьте поле чекбокса пустым.
            </BlockInfo>
          
       {noRequestsLeft ? (
          <p className="text-center">Вам еще не были отправлены заявки</p>
        ) : (
          <>
            {requestdata.map((request, index) => (
<div className={styles.request_field}>
              <RequestsContent
                key={index}
                currentRequest={request}
                handleAccept={handleAccept}
                handleDecline={handleDecline}
              />
              </div>
            ))}
          </>
        )}
          </div>
        );
      return (
        <BaseModal 
          show={show} 
          modalClass={styles.container_modal}
          handleClose={handleClose} 
          title={'Текущие заявки'}
          content={content} 
          darkMode={darkMode}
          sourceData={requests}
        />
      );
    }
}
