import React, { Component } from 'react';
import BaseModal from '../BaseModal/BaseModal';
import BlockInfo from '../../BlockInfo/BlockInfo';
import styles from './ModalRequests.module.css';
import RequestsContent from '../../ProfileBlocks/Teacher/RequestsCard/RequestsContent';
import {Button} from 'react-bootstrap'


export default class ModalRequests extends Component {
  constructor(props) {
    super(props);
    this.state = {
      sortedRequests: this.props.requestdata, // Состояние для отсортированных заявок
    };
  }

  // Функции сортировки
  sortByName = () => {
    const sortedRequests = [...this.state.sortedRequests].sort((a, b) => 
      a.student.surname.localeCompare(b.student.surname)
    );
    this.setState({ sortedRequests });
  };

  sortByGroup = () => {
    const sortedRequests = [...this.state.sortedRequests].sort((a, b) => 
      a.student.group.localeCompare(b.student.group)
    );
    this.setState({ sortedRequests });
  };

  sortByAverageScore = () => {
    const sortedRequests = [...this.state.sortedRequests].sort((a, b) => 
      b.student.average_score - a.student.average_score
    );
    this.setState({ sortedRequests });
  };

  render() {
    const { show, handleClose, darkMode, handleAccept, handleDecline } = this.props;
    const { sortedRequests } = this.state; // Получаем отсортированные заявки из состояния
    
    const content = (
      <div>
        <BlockInfo>
          Вы можете в одностороннем порядке принять или отклонить заявки от выпускников.
          Если Вы принимаете заявку, то можете утвердить тему, поставив галочку в соответствующее окно. 
          Если Вы считаете, что тему нужно подкорректировать, оставьте поле чекбокса пустым.
        </BlockInfo>
        
        <div className="d-flex justify-content-center mb-3">
          <Button variant="primary" className="mx-2" onClick={this.sortByName}>Сортировать по имени</Button>
          <Button variant="primary" className="mx-2" onClick={this.sortByGroup}>Сортировать по группе</Button>
          <Button variant="primary" className="mx-2" onClick={this.sortByAverageScore}>Сортировать по среднему баллу</Button>
        </div>
        
        {sortedRequests.length === 0 ? (
          <p className="text-center">Вам еще не были отправлены заявки</p>
        ) : (
          <>
            {sortedRequests.map((request, index) => (
              <div className={`mx-5 ${styles.request_field}`} key={index}>
                <div className='mx-4'>
                  <RequestsContent
                    currentRequest={request}
                    handleAccept={() => handleAccept(index)}
                    handleDecline={() => handleDecline(index)}
                  />
                </div>
              </div>
            ))}
          </>
        )}
      </div>
    );

    return (
      <BaseModal 
        show={show}
        modalClass={styles.container_modal}
        handleClose={handleClose}
        title={'Текущие заявки'}
        content={content}
        darkMode={darkMode}
      />
    );
  }
}
