import React from 'react';
import { Table } from 'react-bootstrap';

const ViewProtectionSchedulePersons = ({ protection_schedule, students_list_for_schedule }) => {
    const formatDate = (dateString) => {
        const parts = dateString.match(/(\d{2})(\d{2})(\d{4})/);
        if (parts && parts.length === 4) {
            const year = parts[3];
            const month = parseInt(parts[2], 10);
            const day = parseInt(parts[1], 10);
            const months = [
                'января', 'февраля', 'марта', 'апреля', 'мая', 'июня',
                'июля', 'августа', 'сентября', 'октября', 'ноября', 'декабря'
            ];
            return `${day} ${months[month - 1]} ${year}`;
        } else {
            return 'Invalid Date';
        }
    };

    const getFullName = (studentData) => {
        if (studentData && studentData.student) {
            const { last_name, first_name, patronymic } = studentData.student;
            return `${last_name} ${first_name} ${patronymic}`;
        } else {
            return 'Unknown';
        }
    };

    const distributeStudentsByProtectionDays = (schedule) => {
        const groupedSchedule = {};
    
        schedule.forEach(item => {
            if (!item.group) return; // Пропускаем записи без группы
            const dateKey = new Date(item.date).toLocaleDateString(); // Преобразуем дату в строку для сравнения
            const groupName = item.group ? item.group.name : '';
            if (!groupedSchedule[dateKey]) {
                groupedSchedule[dateKey] = {};
            }
            if (!groupedSchedule[dateKey][groupName]) {
                groupedSchedule[dateKey][groupName] = [];
            }
            const students = students_list_for_schedule.filter(student => student.student.group.id === item.group.id);
            students.forEach(student => {
                const fullName = getFullName(student);
                groupedSchedule[dateKey][groupName].push({ fullName, group: groupName, date: formatDate(item.date) });
            });
        });
    
        return groupedSchedule;
    };
    
    
    
    const limitStudentsPerDay = (schedule) => {
        Object.entries(schedule).forEach(([date, groups]) => {
            Object.entries(groups).forEach(([group, students]) => {
                if (students.length > 12) {
                    // Оставляем только первых 12 студентов
                    const firstDayStudents = students.slice(0, 12);
                    const secondDayStudents = students.slice(12);
                    schedule[date][group] = firstDayStudents;
                    const nextDay = getNextDay(date);
                    if (!schedule[nextDay]) {
                        schedule[nextDay] = {};
                    }
                    schedule[nextDay][group] = secondDayStudents;
                }
            });
        });
        return schedule;
    };

    const getNextDay = (dateString) => {
        const date = new Date(dateString);
        date.setDate(date.getDate() + 1);
        return formatDate(date.toLocaleDateString());
    };

    const groupedSchedule = distributeStudentsByProtectionDays(protection_schedule);
    const limitedSchedule = limitStudentsPerDay(groupedSchedule);

    return (
        <div>
            {Object.entries(limitedSchedule).map(([date, groups]) => (
                <div key={date}>
                    <h3>{date}</h3>
                    {Object.entries(groups).map(([group, students]) => (
                        <Table bordered hover key={group}>
                            <thead>
                                <tr>
                                    <th>ФИО</th>
                                    <th>Группа</th>
                                    <th>Дата</th>
                                </tr>
                            </thead>
                            <tbody>
                                {students.map((student, index) => (
                                    <tr key={index}>
                                        <td>{student.fullName}</td>
                                        <td>{student.group}</td>
                                        <td>{student.date}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </Table>
                    ))}
                </div>
            ))}
        </div>
    );
};

export default ViewProtectionSchedulePersons;
