import React, { useState, useEffect } from 'react';
import { useAsyncDebounce } from 'react-table';
import { createPortal } from 'react-dom';
import { Modal, Button, Form } from 'react-bootstrap';
import Sort from '../Sort/Sort'
import filter from '../../../img/filter.png';

const Filter = ({
  column: { filterValue, setFilter, preFilteredRows, id, headerProps },
}) => {
  const [isOpen, setIsOpen] = useState(false);
  const [buttonCoordinates, setButtonCoordinates] = useState({ x: 0, y: 0 });

  useEffect(() => {
    if (isOpen) {
      const handleKeyDown = (e) => {
        if (e.key === 'Escape') {
          setIsOpen(false);
        }
      };

      document.addEventListener('keydown', handleKeyDown);

      return () => {
        document.removeEventListener('keydown', handleKeyDown);
      };
    }
  }, [isOpen]);

  const toggleModal = () => {
    setIsOpen(!isOpen);
  };

  const applyFilter = (value) => {
    setFilter(value || undefined);
    toggleModal();
  };

  const options = React.useMemo(() => {
    const options = new Set();
    preFilteredRows.forEach((row) => {
      options.add(row.values[id]);
    });
    return [...options.values()];
  }, [id, preFilteredRows]);

  const onChange = useAsyncDebounce((value) => {
    applyFilter(value);
  }, 200);

  const handleButtonClick = (event) => {
    const buttonRect = event.target.getBoundingClientRect();
    setButtonCoordinates({ x: buttonRect.x, y: buttonRect.bottom });
    toggleModal();
  };

  const modalElement = isOpen ? (
    <Modal
      show={isOpen}
      onHide={toggleModal}
      centered
      backdrop={false}
      transition={false}
      style={{
        position: 'absolute',
        top: buttonCoordinates.y,
        left: buttonCoordinates.x,
        maxWidth: '200px',
        transition: 'none', // Убираем анимацию
      }}
    >
      <Modal.Header closeButton></Modal.Header>
      <Modal.Body>
        <Form.Control as="select" value={filterValue} onChange={(e) => onChange(e.target.value)}>
          <option value="">All</option>
          {options.map((option, index) => (
            <option key={index} value={option}>
              {option}
            </option>
          ))}
        </Form.Control>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={toggleModal}>
          Close
        </Button>
        <Button variant="primary" onClick={() => applyFilter('')}>
          Clear
        </Button>
      </Modal.Footer>
    </Modal>
  ) : null;

  return (
    <>
      <div style={{ position: 'relative', display: 'inline-block' }} {...headerProps}>
        <Button variant="none" onClick={handleButtonClick}>
          <img src={filter} width={10} height={10} alt="Filter" />
        </Button>
        {createPortal(modalElement, document.body)}
    
      </div>
    </>
  );
};

export default Filter;
