import React from 'react';
import { Table } from 'react-bootstrap';
import styles from './Protection.module.css';

const ViewProtectionSchedulePersons = ({ protection_schedule, students_list_for_schedule, request_list, personal_work_protection_list }) => {

  const getFullName = (studentData) => {
    if (studentData && studentData.user) {
      const { last_name, first_name, patronymic } = studentData.user;
      return `${last_name} ${first_name} ${patronymic}`;
    } else {
      return 'Unknown';
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const day = date.getDate();
    const month = date.getMonth() + 1;
    const year = date.getFullYear();
    return `${day < 10 ? '0' + day : day}.${month < 10 ? '0' + month : month}.${year}`;
  };

  const distributeStudentsByProtectionDays = (schedule) => {
    const sortedSchedule = [...schedule].sort((a, b) => {
      return new Date(a.date) - new Date(b.date);
    });

    const groupedSchedule = {};

    const usedStudents = new Set();

    sortedSchedule.forEach((item) => {
      if (!item.group) return;

      const dateKey = item.date;
      const formattedDate = formatDate(dateKey);

      const groupName = item.group.name;

      if (!groupedSchedule[formattedDate]) {
        groupedSchedule[formattedDate] = {};
      }

      if (!groupedSchedule[formattedDate][groupName]) {
        groupedSchedule[formattedDate][groupName] = { students: [] };
      }

      const students = students_list_for_schedule.filter((student) => student.group.id === item.group.id);
      const availableStudents = students.filter((student) => !usedStudents.has(student.user.username));
      const studentsToAdd = availableStudents.slice(0, 12);

      studentsToAdd.forEach((student) => {
        const matchingRequest = request_list.find((req) => req.student.username === student.user.username);

        if (!matchingRequest) {
          return;
        }

        // Находим соответствующую запись в personal_work_protection_list
        const matchingPersonal = personal_work_protection_list.find((pw) => pw.request.id === matchingRequest.id);

        if (!matchingPersonal) {
          return; // Если нет соответствующей записи, пропускаем студента
        }

        const personalTime = matchingPersonal.personal_time ? matchingPersonal.personal_time.substring(0, 5) : '';
        const additionalInfo = matchingPersonal.additional_information || '';

        const teacherFullName = matchingRequest.teacher
          ? `${matchingRequest.teacher.last_name} ${matchingRequest.teacher.first_name} ${matchingRequest.teacher.patronymic}`
          : 'Unknown';

        groupedSchedule[formattedDate][groupName].students.push({
          ...student,
          personal_time: personalTime,
          additional_information: additionalInfo,
          vkr_theme: matchingRequest.theme ? matchingRequest.theme.name : 'Unknown',
          teacher_full_name: teacherFullName,
        });

        usedStudents.add(student.user.username);
      });

      // Сортировка студентов по алфавиту по полному имени
      groupedSchedule[formattedDate][groupName].students.sort((a, b) => {
        const nameA = getFullName(a).toUpperCase();
        const nameB = getFullName(b).toUpperCase();
        if (nameA < nameB) {
          return -1;
        }
        if (nameA > nameB) {
          return 1;
        }
        return 0;
      });
    });

    return groupedSchedule;
  };

  const groupedSchedule = distributeStudentsByProtectionDays(protection_schedule);

  return (
    <div>
      <h4 className='text-center'> График защит ВКР по фамильный</h4>
      <div className='mx-md-5'>
        {Object.entries(groupedSchedule).map(([date, groups]) => (
          <div key={date}>
            <h3>{date}</h3>
            {Object.entries(groups).map(([group, { students }]) => (
              <React.Fragment key={`${date}-${group}`}>
                {students.length > 0 && (
                  <Table bordered hover responsive className={styles.protectionTable}>
                    <thead>
                      <tr>
                        <th width='15%'>ФИО</th>
                        <th>Группа</th>
                        <th>Дата</th>
                        <th>Время</th>
                        <th width='30%'>Тема ВКР</th>
                        <th>Руководитель</th>
                      </tr>
                    </thead>
                    <tbody>
                      {students.map((student, index) => (
                        <tr key={index}>
                          <td>{getFullName(student)}</td>
                          <td>{group}</td>
                          <td>{date}</td>
                          <td>{student.personal_time}</td>
                          <td>{student.vkr_theme}</td>
                          <td>{student.teacher_full_name}</td>
                        </tr>
                      ))}
                    </tbody>
                  </Table>
                )}
              </React.Fragment>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
};

export default ViewProtectionSchedulePersons;
