import React, { Component } from 'react'
import BaseModal from '../BaseModal/BaseModal'
import { Modal, Table, Col, Row, Button } from 'react-bootstrap';
import styles from './ScheduleFQWModal.module.css'
import styles_base from '../BaseModal/BaseModal.module.css'
import generateTask from './GenerateTask';

export default class ScheduleFQWModal extends Component {
  render() {

    const handleClick = async () => {
        await generateTask({request_data_task});
      };
    const { show, handleClose, darkMode, schedule_stud_prof, request_data_task} = this.props;
    console.log (schedule_stud_prof)
    const content = (
        <div className='p-2'>
        <Table  bordered hover>
        <thead>
          <tr>
            <th>Этап</th>
            <th>Начало</th>
            <th>Конец</th>
            <th>Результат</th>
            <th>Отметка о выполнении</th>
            <th>Пропуск</th>
          </tr>
        </thead>
        <tbody>
          {schedule_stud_prof.map(stage => (
            <tr key={stage.id}>
              <td>{stage.stage_name}</td>
              <td>{stage.start}</td>
              <td>{stage.end}</td>
              <td>{stage.result}</td>
              <td>{stage.completion_mark}</td>
              <td>{stage.absence}</td>
            </tr>
          ))}
        </tbody>
      </Table>
</div>
    );

    const footer = (
        <Row className="justify-content-between px-3">
            <Col md={5}>
          <Button variant='none' className={styles_base.button_cancel} onClick={handleClick()}>Экспортировать</Button>
          </Col>
          <Col md={5}>
          <Button variant='none' className={styles_base.button_next} onClick={handleClose}>ОКЭЙ</Button>
          </Col>
          </Row>
      );

    return (
      <BaseModal show={this.props.show} 
      handleClose={this.props.handleClose} 
      title={'График работы над ВКР'}
      content={content} 
      darkMode={darkMode}
      sourceData={schedule_stud_prof}
      modalClass={styles.container_modal}
      footer={footer}
      request_data_task={request_data_task}
      >
        </BaseModal>
    )
  }
}
