import React, { Component } from 'react';
import BaseModal from '../BaseModal/BaseModal';
import { Row, Col, Button } from 'react-bootstrap';
import styles_base from '../BaseModal/BaseModal.module.css';
import ScheduleFQWModal from '../ScheduleFQWModal/ScheduleFQWModal';
import { Link } from 'react-router-dom';
import axios from 'axios';

export default class ModalViewGraphExists extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showScheduleModal: false,
      scheduleStudProf: [] // Стейт для хранения полученных данных
    };
  }

  componentDidMount() {

    this.fetchScheduleStudProf();
  }


  fetchScheduleStudProf = async () => {
    try {
      const { requestId } = this.props; 
      const response = await axios.get(`http://127.0.0.1:8000/v1/main/teacher/work_schedule_by_request/${requestId}`);
      const data = response.data;
      this.setState({ scheduleStudProf: data });
    } catch (error) {
      console.error('Error fetching schedule:', error);
    }
  };

  handleScheduleModalOpen = () => {
    this.setState({ showScheduleModal: true });
  };

  handleScheduleModalClose = () => {
    this.setState({ showScheduleModal: false });
  };

  render() {
    const { show, handleClose, darkMode, teacherData } = this.props;
    const { showScheduleModal, scheduleStudProf } = this.state;

    // Определение содержимого модального окна
    const content =
      scheduleStudProf.length > 0 ? 'График был создан' : 'График работы над ВКР еще не был создан';

    // Определение подвала модального окна
    const footer =
      scheduleStudProf.length > 0 ? (
        <Row className="justify-content-between px-3">
          <Col md={5}>
            <Button
              variant="none"
              onClick={this.handleScheduleModalOpen}
              className={styles_base.button_cancel}
            >
              Просмотреть
            </Button>
          </Col>
          <Col md={5}>
            <Button variant="none" className={styles_base.button_next} onClick={handleClose}>
              Отмена
            </Button>
          </Col>
        </Row>
      ) : (
        <Row className="justify-content-between px-3">
          <Col>
            <Link to="/create_schedule_work_fqw">
              <Button variant="none" className={styles_base.button_next} onClick={handleClose}>
                Создать
              </Button>
            </Link>
          </Col>
          <Col md={5}>
            <Button variant="none" className={styles_base.button_next} onClick={handleClose}>
              ОК
            </Button>
          </Col>
        </Row>
      );

    return (
      <>
        <BaseModal
          show={show}
          handleClose={handleClose}
          title={'График работы над ВКР'}
          content={content}
          darkMode={darkMode}
          sourceData={scheduleStudProf}
          footer={footer}
          request_data_task={request_data_task}
          data={teacherData}
        />
        <ScheduleFQWModal
          show={showScheduleModal}
          handleClose={this.handleScheduleModalClose}
          darkMode={false}
          schedule_stud_prof={scheduleStudProf}
          request_data_task={request_data_task}
          data={teacherData}
        />
      </>
    );
  }
}
