import React, { useState } from 'react';

function PublishOrdersForm() {
  const [dragging, setDragging] = useState(false);

  const handleDragStart = (event) => {
    event.preventDefault();
    setDragging(true);
  };

  const handleDragEnd = (event) => {
    event.preventDefault();
    setDragging(false);
  };

  const handleDrop = (event) => {
    event.preventDefault();
    setDragging(false);
    // Обработка перетаскивания: получение данных и выполнение необходимых действий
    const data = event.dataTransfer.getData('text/plain');
    console.log('Dropped data:', data);
  };

  return (
    <div
      onDragOver={(event) => event.preventDefault()}
      onDrop={handleDrop}
    >
      <div
        draggable
        onDragStart={handleDragStart}
        onDragEnd={handleDragEnd}
        style={{ border: dragging ? '2px dashed blue' : '2px solid black', padding: '10px', marginBottom: '10px' }}
      >
        Drag me!
      </div>
      {/* Здесь можно добавить другие элементы для перетаскивания */}
    </div>
  );
}

export default PublishOrdersForm;
