import React, { Component, useState } from 'react'
import styles from './EditProfileStudentForm.module.css';
import styles_base from '../../Forms/FormsBase.module.css';
import prev from './prev.png'; // Путь к изображению предыдущей стрелки
import next from './next.png';
import { Button, Container, Row, Form, Col, Image } from 'react-bootstrap';
function EditProfileStudentForm() {
  const [step, setStep] = useState(1);

  const nextStep = () => {
    setStep(step + 1);
  };

  const prevStep = () => {
    setStep(step - 1);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission logic here
  };

  return (
    <Container>
      <div className={styles_base.card}>
        <form className="px-4 mx-2 my-1" onSubmit={handleSubmit}>
          {/* Form steps */}
          {/* Step 1 */}
          <div className={step === 1 ? 'step active' : 'step'} id="step1">
            {/* Form fields for step 1 */}
            {/* Buttons for navigating between steps */}
            <Row className="justify-content-center pt-3">
              <Col>
                <Button className="prev" onClick={prevStep}>
                  <img src={prev} width="10%" alt="Предыдущий" />
                </Button>
              </Col>
              <Col>
                <Button className="next" onClick={nextStep}>
                  <img src={next} width="10%" alt="Следующий" />
                </Button>
              </Col>
            </Row>
          </div>
          {/* Step 2 */}
          <div className={step === 2 ? 'step active' : 'step'} id="step2">
            {/* Form fields for step 2 */}
            {/* Buttons for navigating between steps */}
            <Row className="justify-content-center pt-3">
              <Col>
                <Button className="prev" onClick={prevStep}>
                  <img src={prev} width="10%" alt="Предыдущий" />
                </Button>
              </Col>
              <Col>
                <Button className="next" onClick={nextStep}>
                  <img src={next} width="10%" alt="Следующий" />
                </Button>
              </Col>
            </Row>
          </div>
          {/* Step 3 */}
          <div className={step === 3 ? 'step active' : 'step'} id="step3">
            {/* Form fields for step 3 */}
            {/* Buttons for navigating between steps */}
            <Row className="justify-content-between pt-3">
              <Col>
                <Button className="prev" onClick={prevStep}>
                  <img src={prev} width="10%" alt="Предыдущий" />
                </Button>
              </Col>
              <Col>
                <Button type="submit" className="button-form">Отправить</Button>
              </Col>
            </Row>
          </div>
        </form>
      </div>
    </Container>
  );
}

export default EditProfileStudentForm;
