import React, { useState } from 'react';
import BlockInfo from '../../BlockInfo/BlockInfo';
import plus from '../../../img/plus.png';
import del from '../../../img/del.png';
import edit from '../../../img/edit.png';
import styles from '../EditThemeList/EditThemeList.module.css';
import EditTheme from '../../Forms/EditTheme/EditTheme';

function EditThemeListTeacherForm({ tableData }) {
  const [data, setData] = useState(tableData || []);
  const [selectedRowData, setSelectedRowData] = useState(null); // Состояние для хранения данных выбранной строки
  const [isEditing, setIsEditing] = useState(false); // Состояние для отслеживания открытой формы редактирования

  const addRow = () => {
    const newRow = {
      theme: '',
      organization: { name: '', responsible: '', position: '', additional: '' },
      occupied: '',
      actions: [{ type: "edit"}, { type: "delete"}]
    };
    setData([newRow, ...data]);
  };

  const handleEditClick = (index) => {
    setSelectedRowData(data[index]);
    setIsEditing(true); // Открываем форму редактирования при нажатии на кнопку "Редактировать"
  };

  const handleDeleteClick = (index) => {
    const newData = [...data];
    newData.splice(index, 1);
    setData(newData);

    if (selectedRowData && selectedRowData === data[index]) {
      setSelectedRowData(null);
    }
  };

  const handleSave = (updatedData) => {
    const newData = data.map(item => item === selectedRowData ? updatedData : item);
    setData(newData);
    setIsEditing(false); // Закрываем форму редактирования после сохранения изменений
  };

  const handleCancel = () => {
    setIsEditing(false); // Закрываем форму редактирования без сохранения изменений
  };

  return (
    <>
      <h4 className="mb-4 text-center">Редактирование списка тем ВКР</h4>
      <BlockInfo>
        Вы можете редактировать уже созданные темы ВКР и добавлять новые. С помощью фильтрации можете выбрать нужные темы: по названию, наличию организации, а также свободные и занятые темы.
      </BlockInfo>

      {isEditing ? (
        <EditTheme data={selectedRowData} onSave={handleSave} onCancel={handleCancel} />
      ) : (
        <table width="100%" className={styles.table_themes}>
          <thead className="text-center">
            <tr>
              <th width="60%" rowSpan="2">Наименование темы ВКР</th>
              <th width="25%" colSpan="4" className="no-filter">Организация</th>
              <th rowSpan="2">Занята</th>
              <th rowSpan="2" className="no-filter">Действия</th>
              <th rowSpan="2" className="no-filter">
                <img id="addRowButton" src={plus} onClick={addRow} width="30" height="30" alt="Add Row" />
              </th>
            </tr>
            <tr>
              <th>Наименование</th>
              <th>Ответственный</th>
              <th>Должность</th>
              <th>Дополнительно</th>
            </tr>
          </thead>
          <tbody>
            {data.map((item, index) => (
              <React.Fragment key={index}>
                <tr>
                  <td>{item.theme}</td>
                  <td>{item.organization.name}</td>
                  <td>{item.organization.responsible}</td>
                  <td>{item.organization.position}</td>
                  <td>{item.organization.additional}</td>
                  <td>{item.occupied}</td>
                  <td>
                    <img key="edit" className="action-button" src={edit} onClick={() => handleEditClick(index)} width="20" height="20" alt="edit" />
                    <img key="delete" className="action-button" src={del} onClick={() => handleDeleteClick(index)} width="20" height="20" alt="delete" />
                  </td>
                </tr>
              </React.Fragment>
            ))}
          </tbody>
        </table>
      )}
    </>
  );
}

export default EditThemeListTeacherForm;
