import React, { useState } from 'react';
import { Form, Row, Col, Button } from 'react-bootstrap';
import styles from './EditTheme.module.css'

function EditTheme({data}) {
    const [theme, setTheme] = useState(data.theme || '');
  const [haveOrganization, setHaveOrganization] = useState('');
  const [organizationName, setOrganizationName] = useState('');
  const [fioResponsible, setFioResponsible] = useState('');
  const [postResponsible, setPostResponsible] = useState('');
  const [additionalResponsible, setAdditionalResponsible] = useState('');
  const [isFree, setIsFree] = useState(false);
  const [originalData, setOriginalData] = useState(data); 

  const handleSaveChanges = () => {
    const updatedData = {
      theme,
      organization: {
        name: organizationName,
        responsible: fioResponsible,
        position: postResponsible,
        additional: additionalResponsible
      },
      occupied: isFree ? 'нет' : 'да',
      actions: data.actions
    };
    setOriginalData(updatedData); 
  };

  const handleCancel = () => {
    setTheme(originalData.theme);
    setOrganizationName(originalData.organization.name);
    setFioResponsible(originalData.organization.responsible);
    setPostResponsible(originalData.organization.position);
    setAdditionalResponsible(originalData.organization.additional);
    setIsFree(originalData.occupied === 'нет');
  };

  return (
    <Form className="py-2 my-2 mx-5">
      <Form.Group as={Row}>
        <Form.Label column sm={4}>Тема ВКР</Form.Label>
        <Col sm={8}>
          <Form.Control
            type="text"
            value={theme}
            onChange={(e) => setTheme(e.target.value)}
            placeholder="тема ВКР"
          />
        </Col>
      </Form.Group>
      <Form.Group as={Row}>
        <Form.Label column sm={4}>Связь темы ВКР с организацией</Form.Label>
        <Col sm={8} className="d-flex align-items-center">
          <Form.Check
            inline
            type="radio"
            label="связана"
            checked={haveOrganization === 'yes'}
            onChange={() => setHaveOrganization('yes')}
          />
          <Form.Check
            inline
            type="radio"
            label="не связана"
            checked={haveOrganization === 'no'}
            onChange={() => setHaveOrganization('no')}
          />
        </Col>
      </Form.Group>
      {haveOrganization === 'yes' && (
        <>
          <Form.Group as={Row} className="organization-fields">
            <Form.Label column sm={4}>Наименование организации</Form.Label>
            <Col sm={8}>
              <Form.Control
                list="organization_name"
                value={organizationName}
                onChange={(e) => setOrganizationName(e.target.value)}
              />
              <datalist id="organization_name">
                <option value="Значение 1" />
                <option value="Значение 2" />
                <option value="Значение 3" />
              </datalist>
            </Col>
          </Form.Group>
        
          <Form.Group as={Row} className="organization-fields">
            <Form.Label column sm={4}>ФИО ответственного</Form.Label>
            <Col sm={8}>
              <Form.Control
                list="fioResponsible"
                value={fioResponsible}
                onChange={(e) => setOrganizationName(e.target.value)}
              />
              <datalist id="fioResponsible">
                <option value="Значение 1" />
                <option value="Значение 2" />
                <option value="Значение 3" />
              </datalist>
            </Col>
          </Form.Group>

          <Form.Group as={Row} className="organization-fields">
            <Form.Label column sm={4}>Должность ответственного</Form.Label>
            <Col sm={8}>
              <Form.Control
                list="postResponsible"
                value={postResponsible}
                onChange={(e) => setOrganizationName(e.target.value)}
              />
              <datalist id="postResponsible">
                <option value="Значение 1" />
                <option value="Значение 2" />
                <option value="Значение 3" />
              </datalist>
            </Col>
          </Form.Group>

          <Form.Group as={Row}>
        <Form.Label column sm={4}>Дополнительно</Form.Label>
        <Col sm={8}>
          <Form.Control
            type="text"
            value={additionalResponsible}
            onChange={(e) => setTheme(e.target.value)}
            placeholder="дополнительно"
          />
        </Col>
      </Form.Group>

        </>
      )}
      <Form.Group as={Row} className="organization-fields">
      <Form.Label column sm={4}>Занято</Form.Label>
      <Col sm={8}>
        <Form.Check type="checkbox" label="Занято" name="is_free" />
      </Col>
    </Form.Group>

      <div className="row justify-content-center">
        <div className="col-md-4">
          <Button variant="none" className={styles.cancel_button} onClick={handleCancel}>Отмена</Button>
        </div>
        <div className="col-md-4">
          <Button variant="none"className={styles.save_button} onClick={handleSaveChanges}>Сохранить изменения</Button>
        </div>
      </div>
    </Form>
  );
}

export default EditTheme;
