import React, { useState, useRef } from 'react';
import BlockInfo from '../../BlockInfo/BlockInfo';
import styles from './PublishOrdersForm.module.css';

function PublishOrdersForm() {
  const [dragging, setDragging] = useState(false);
  const [selectedFiles, setSelectedFiles] = useState([]);
  const inputRef = useRef(null);

  const handleDragEnter = (event) => {
    event.preventDefault();
    setDragging(true);
  };

  const handleDragLeave = (event) => {
    event.preventDefault();
    setDragging(false);
  };

  const handleDragOver = (event) => {
    event.preventDefault();
  };

  const handleDrop = (event) => {
    event.preventDefault();
    setDragging(false);
    const files = Array.from(event.dataTransfer.files);
    setSelectedFiles([...selectedFiles, ...files.map(file => file.name)]);
  };

  const handleFileInputChange = (event) => {
    const files = Array.from(event.target.files);
    setSelectedFiles([...selectedFiles, ...files.map(file => file.name)]);
  };

  return (
    <>
      <h4 className="mb-4 text-center">Загрузка приказов</h4>
      <BlockInfo>
        Загрузите приказы в формате pdf и они станут доступны всем пользователям в системе
      </BlockInfo>
      <div
        onDragEnter={handleDragEnter}
        onDragLeave={handleDragLeave}
        onDragOver={handleDragOver}
        onDrop={handleDrop}
        className={styles.form_drag}
      >
        {dragging ? 'Файл выбран' : 'Перетащите файл сюда или кликните для выбора'}
      </div>
      <input
        type="file"
        accept=".pdf"
        ref={inputRef}
        style={{ display: 'none' }}
        onChange={handleFileInputChange}
        multiple
      />
      {selectedFiles.length > 0 && (
        <div>
          <h5>Выбранные файлы:</h5>
          <ul>
            {selectedFiles.map((fileName, index) => (
              <li key={index}>{fileName}</li>
            ))}
          </ul>
        </div>
      )}
    </>
  );
}

export default PublishOrdersForm;
