import React from 'react';
import { Table } from 'react-bootstrap';

function ViewProtectionScheduleTable({ protection_schedule }) {
  // Helper function to convert date format
  const formatDate = (dateString) => {
    const months = [
      'января', 'февраля', 'марта', 'апреля', 'мая', 'июня',
      'июля', 'августа', 'сентября', 'октября', 'ноября', 'декабря'
    ];
    const day = dateString.slice(0, 2);
    const month = months[parseInt(dateString.slice(2, 4), 10) - 1];
    const year = dateString.slice(4, 8);
    return `${day} ${month} ${year}`;
  };

  const generateHeading = (group) => {
    const { name, speciality } = group;
    const { code: specialityCode, name: specialityName, field_of_study } = speciality;
    const { code: fieldOfStudyCode, name: fieldOfStudyName } = field_of_study;

    if (name.includes('Б')) {
      return `Бакалавры по специальности ${specialityCode} "${specialityName}" по программе ${fieldOfStudyCode} "${fieldOfStudyName}"`;
    } else if (name.includes('М')) {
      return `Магистры по специальности ${specialityCode} "${specialityName}" по программе ${fieldOfStudyCode} "${fieldOfStudyName}"`;
    } else {
      return `Группа ${name}`;
    }
  };

  // Group by group name
  const groupedByGroupName = protection_schedule.reduce((acc, item) => {
    const groupName = item.group.name;
    if (!acc[groupName]) {
      acc[groupName] = [];
    }
    acc[groupName].push(item);
    return acc;
  }, {});

  // Group by date
  const groupedByDate = protection_schedule.reduce((acc, item) => {
    const date = item.date;
    if (!acc[date]) {
      acc[date] = [];
    }
    acc[date].push(item);
    return acc;
  }, {});

  const processedGroups = new Set();

  return (
    <>
      <h4 className="mb-4 text-center">График защит ВКР</h4>
      <div className='mx-5'>
        {Object.keys(groupedByDate).map((date, index) => {
          const dateItems = groupedByDate[date];

          // Filter out items that have been processed already
          const unprocessedItems = dateItems.filter(item => !processedGroups.has(item.group.name));

          if (unprocessedItems.length > 1) {
            const uniqueGroupNames = [...new Set(unprocessedItems.map(item => item.group.name))];
            uniqueGroupNames.forEach(groupName => processedGroups.add(groupName));

            return (
              <div key={index} style={{ marginBottom: '20px' }}>
                <h6>Дата: {formatDate(date)}</h6>
                {uniqueGroupNames.map((groupName, groupIdx) => {
                  const groupItem = unprocessedItems.find(item => item.group.name === groupName);
                  const heading = generateHeading(groupItem.group);
                  return <h6 key={groupIdx}>{heading}</h6>;
                })}
                <Table bordered style={{ width: '100%' }}>
                  <thead>
                    <tr>
                      <th>ПП</th>
                      <th>Дата</th>
                      <th>Время</th>
                      <th>Аудитория</th>
                      <th>Уч. группа</th>
                    </tr>
                  </thead>
                  <tbody>
                    {unprocessedItems.map((item, idx) => (
                      <tr key={item.id}>
                        <td>{idx + 1}</td>
                        <td>{formatDate(item.date)}</td>
                        <td>9:00</td>
                        <td>{item.audience_number}</td>
                        <td>{item.group.name}</td>
                      </tr>
                    ))}
                  </tbody>
                </Table>
              </div>
            );
          } else {
            return null;
          }
        })}

        {Object.keys(groupedByGroupName).map((groupName, index) => {
          if (!processedGroups.has(groupName)) {
            const groupItems = groupedByGroupName[groupName];
            processedGroups.add(groupName);

            // Group by date for the same group
            const groupedByDateForGroup = groupItems.reduce((acc, item) => {
              const date = item.date;
              if (!acc[date]) {
                acc[date] = [];
              }
              acc[date].push(item);
              return acc;
            }, {});

            const group = groupItems[0].group;
            const heading = generateHeading(group);

            return (
              <div key={index} style={{ marginBottom: '20px' }}>
                <h6>{heading}</h6>
                <Table bordered style={{ width: '100%' }}>
                  <thead>
                    <tr>
                      <th>ПП</th>
                      <th>Дата</th>
                      <th>Время</th>
                      <th>Аудитория</th>
                      <th>Уч. группа</th>
                    </tr>
                  </thead>
                  <tbody>
                    {Object.keys(groupedByDateForGroup).map((date, dateIdx) => {
                      const dateItems = groupedByDateForGroup[date];

                      return dateItems.map((item, idx) => (
                        <tr key={item.id}>
                          <td>{dateIdx * dateItems.length + idx + 1}</td>
                          <td>{formatDate(item.date)}</td>
                          <td>9:00</td>
                          <td>{item.audience_number}</td>
                          <td>{groupName}</td>
                        </tr>
                      ));
                    })}
                  </tbody>
                </Table>
              </div>
            );
          } else {
            return null;
          }
        })}
      </div>
    </>
  );
}

export default ViewProtectionScheduleTable;
