import React, { useState } from 'react';
import { Form, Button, Row, Col } from 'react-bootstrap';
import plus from '../../../../img/plus.png';
import del from '../../../../img/del.png';
import edit from '../../../../img/edit.png';
import styles from '../Teacher/EditThemeList.module.css';

const EditThemesListSecretaryForm = ({ themesList: initialThemesList, teachersList }) => {
  const [themesList, setThemesList] = useState(initialThemesList?.theme_list || []);
  const [editingIndex, setEditingIndex] = useState(null);
  const [editingData, setEditingData] = useState(null);
  const [editFormVisibleIndex, setEditFormVisibleIndex] = useState(null);
  const [isLinked, setIsLinked] = useState(false);
  const [filterAuthor, setFilterAuthor] = useState('');

  const [authorType, setAuthorType] = useState('');
  const [selectedTeacher, setSelectedTeacher] = useState(null);
  const teach_list = teachersList.teachers_list
  console.log(teach_list)
  const addRow = () => {
    const newRow = {
      id: themesList.length ? Math.max(...themesList.map(item => item.id)) + 1 : 1,
      name: '',
      teacher: {
        user: '' // Устанавливаем текущего пользователя в качестве преподавателя
      },
      company: {
        name: '',
        last_name_of_responsible: '',
        first_name_of_responsible: '',
        patronymic_of_responsible: '',
        job_title_of_responsible: '',
        additional_information: '',
      },
      free: true,
      fqw_by_application: '',
      reviewer: {
        user: {
          id: null,
          last_name: null,
          first_name: null,
          patronymic: null
        }
      },
      by_student: false,
      department: false,
    };

    const updatedThemesList = [newRow, ...themesList];
    setThemesList(updatedThemesList);
    setEditingIndex(newRow.id);
    setEditingData(newRow);
    setAuthorType('');
  };

  const handleSave = () => {
    const updatedThemesList = themesList.map(item =>
      item.id === editingIndex ? { ...item, ...editingData } : item
    );
    setThemesList(updatedThemesList);
    setEditFormVisibleIndex(null); // Устанавливаем null, чтобы скрыть форму редактирования
    setEditingIndex(null);
    setEditingData(null);
  };

  const handleFilterAuthorChange = (e) => {
    setFilterAuthor(e.target.value);
  };

  const filteredThemesList = themesList.filter(item => {
    // Если это новая строка (без автора), показываем ее в любом случае
    if (!item.teacher?.user?.first_name && !item.teacher?.user?.last_name && !item.teacher?.user?.patronymic && !item.by_student && !item.department) {
      return true;
    }

    if (item.teacher?.user?.first_name && item.teacher?.user?.last_name && item.teacher?.user?.patronymic) {
      return `${item.teacher.user.last_name} ${item.teacher.user.first_name} ${item.teacher.user.patronymic}`.toLowerCase().includes(filterAuthor.toLowerCase());
    } else if (item.by_student) {
      return 'предложено студентом'.toLowerCase().includes(filterAuthor.toLowerCase());
    } else if (item.department) {
      return 'кафедра сии'.toLowerCase().includes(filterAuthor.toLowerCase());
    } else {
      return true;
    }
  });

  const handleEditClick = (index) => {
    const themeToEdit = themesList.find(item => item.id === index);
    setEditingIndex(index);
    setEditingData(themeToEdit);
    setEditFormVisibleIndex(index);
    setIsLinked(!!themeToEdit.company?.name);
    setAuthorType(
      themeToEdit.by_student ? 'student' :
      themeToEdit.department ? 'department' :
      'teacher'
    );
  };

  const handleCancel = () => {
    setEditFormVisibleIndex(null);
    if (editingIndex === null) {
      const newData = themesList.filter(item => item.id !== editingIndex);
      setThemesList(newData);
    }
    setEditingIndex(null);
  };

  const handleDeleteClick = (index) => {
    const newData = themesList.filter(item => item.id !== index);
    setThemesList(newData);
    if (editFormVisibleIndex === index) {
      setEditFormVisibleIndex(null);
    }
    setEditingIndex(null);
  };

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    const updatedEditingData = {
      ...editingData,
      [name]: type === 'checkbox' ? checked : value,
    };
    setEditingData(updatedEditingData);
  };

  const handleCompanyChange = (e) => {
    const { name, value } = e.target;
    const updatedEditingData = {
      ...editingData,
      company: {
        ...editingData.company,
        [name]: value,
      },
    };
    setEditingData(updatedEditingData);
  };

  const handleReviewerChange = (e) => {
    const { name, value } = e.target;
    const updatedReviewer = {
      ...editingData.reviewer.user,
      [name]: value,
    };
    const updatedEditingData = {
      ...editingData,
      reviewer: {
        user: updatedReviewer,
      },
    };
    setEditingData(updatedEditingData);
  };

  const handleRadioChange = (value) => {
    setIsLinked(value === 'yes');
    if (value === 'no') {
      // Если выбрана опция "Нет", очищаем поля организации
      const updatedEditingData = {
        ...editingData,
        company: {
          name: '',
          last_name_of_responsible: '',
          first_name_of_responsible: '',
          patronymic_of_responsible: '',
          job_title_of_responsible: '',
          additional_information: '',
        },
      };
      setEditingData(updatedEditingData);
    }
  };

  const handleTeacherSelectChange = (e) => {
    const teacherId = e.target.value;
    const selectedTeacher = teach_list.find(teacher => teacher.user.id === parseInt(teacherId));

    // Установите выбранного преподавателя в состояние selectedTeacher
    setSelectedTeacher(selectedTeacher);

    const updatedEditingData = {
      ...editingData,
      teacher: {
        user: selectedTeacher.user
      }
    };

    setEditingData(updatedEditingData);
  };

  const handleAuthorTypeChange = (e) => {
    const { value } = e.target;
    setAuthorType(value);
    
    // Сбрасываем выбранных преподавателя, если тип автора изменен
    setSelectedTeacher(null);
    
    // Устанавливаем соответствующие флаги в зависимости от выбранного типа автора
    switch (value) {
      case 'teacher':
        setEditingData(prevState => ({
          ...prevState,
          by_student: false,
          department: false
        }));
        break;
      case 'department':
        setEditingData(prevState => ({
          ...prevState,
          by_student: false,
          department: true
        }));
        break;
      case 'student':
        setEditingData(prevState => ({
          ...prevState,
          by_student: true,
          department: false
        }));
        break;
      default:
        break;
    }
  };

  const getTeacherName = (item) => {
    if (item.teacher?.user?.first_name && item.teacher?.user?.last_name && item.teacher?.user?.patronymic) {
      return `${item.teacher.user.last_name} ${item.teacher.user.first_name} ${item.teacher.user.patronymic}`;
    } else if (item.by_student) {
      return 'Предложено студентом';
    } else if (item.department) {
      return 'Кафедра СИИ';
    } else {
      return ''; // Для новых записей без автора
    }
  };

  return (
    <>
      <h4 className="mb-4 text-center">Редактирование списка тем ВКР</h4>
      <Form.Group as={Row} controlId="filterAuthor" className="justify-content-center">
        <Form.Label column sm={2} className="text-center my-3">Фильтр по автору</Form.Label>
        <Col sm={6}>
          <Form.Control
            type="text"
            value={filterAuthor}
            onChange={handleFilterAuthorChange}
            placeholder="Введите имя автора для фильтрации"
            className={styles.block_form}
          />
        </Col>
      </Form.Group>

      <div className={styles.catch}>
        <table className={styles.table_themes}>
          <thead className="text-center">
            <tr>
              <th rowSpan="2" width="10%">Автор</th>
              <th rowSpan="2" width="50%">Наименование темы ВКР</th>
              <th colSpan="4" className="no-filter">Организация</th>
              <th rowSpan="2" width="5%">ВКР по заявке</th>
              <th rowSpan="2" width="5%">Ревьювер</th>
              <th rowSpan="2">Занята</th>
              <th rowSpan="2" className="no-filter" width="10%">Действия</th>
              <th>  <img src={plus} alt="add"  onClick={addRow} width="30" height="30"/></th>
            </tr>
            <tr>
              <th className="no-filter" width="5%">Название</th>
              <th className="no-filter" width="5%">Фамилия</th>
              <th className="no-filter" width="5%">Имя</th>
              <th className="no-filter" width="5%">Отчество</th>
            </tr>
          </thead>
          <tbody className="text-center">
            {filteredThemesList.map((item, index) => (
              <React.Fragment key={item.id}>
                <tr>
                  <td>{getTeacherName(item)}</td>
                  <td>{item.name}</td>
                  <td>{item.company?.name}</td>
                  <td>{item.company?.last_name_of_responsible}</td>
                  <td>{item.company?.first_name_of_responsible}</td>
                  <td>{item.company?.patronymic_of_responsible}</td>
                  <td>{item.fqw_by_application}</td>
                  <td>{item.reviewer?.user?.last_name && item.reviewer?.user?.first_name && item.reviewer?.user?.patronymic ? `${item.reviewer.user.last_name} ${item.reviewer.user.first_name} ${item.reviewer.user.patronymic}` : ''}</td>
                  <td>{item.free ? 'Свободна' : 'Занята'}</td>
                  <td>
                    <Button onClick={() => handleEditClick(item.id)} variant='light'>
                      <img src={edit} alt="edit" width="20" height="20"/>
                    </Button>
                    <Button onClick={() => handleDeleteClick(item.id)} variant='light'>
                      <img src={del} alt="delete" width="20" height="20" />
                    </Button>
                  </td>
                </tr>
                {editFormVisibleIndex === item.id && (
                  <tr>
                    <td colSpan="10">
                      <Form className={styles.block_form}>
                        <Form.Row>
                          <Col>
                            <Form.Group controlId="formThemeName">
                              <Form.Label>Наименование темы ВКР</Form.Label>
                              <Form.Control
                                type="text"
                                name="name"
                                value={editingData.name}
                                onChange={handleInputChange}
                                className={styles.block_form}
                              />
                            </Form.Group>
                          </Col>
                          <Col>
                            <Form.Group controlId="formAuthorType">
                              <Form.Label>Автор</Form.Label>
                              <Form.Control
                                as="select"
                                value={authorType}
                                onChange={handleAuthorTypeChange}
                                className={styles.block_form}
                              >
                                <option value="teacher">Преподаватель</option>
                                <option value="department">Кафедра СИИ</option>
                                <option value="student">Предложено студентом</option>
                              </Form.Control>
                            </Form.Group>
                          </Col>
                        </Form.Row>
                       
                        <Form.Group>
                          <Form.Label>Свободна</Form.Label>
                          <Form.Check
                            type="checkbox"
                            name="free"
                            checked={editingData.free}
                            onChange={handleInputChange}
                          />
                        </Form.Group>
                        <Form.Group>
                          <Form.Label>ВКР по заявке</Form.Label>
                          <Form.Control
                            type="text"
                            name="fqw_by_application"
                            value={editingData.fqw_by_application}
                            onChange={handleInputChange}
                            className={styles.block_form}
                          />
                        </Form.Group>
                        <Form.Group>
                          <Form.Label>Связана с организацией</Form.Label>
                          <Form.Check
                            type="radio"
                            name="isLinked"
                            label="Да"
                            value="yes"
                            checked={isLinked}
                            onChange={() => handleRadioChange('yes')}
                          />
                          <Form.Check
                            type="radio"
                            name="isLinked"
                            label="Нет"
                            value="no"
                            checked={!isLinked}
                            onChange={() => handleRadioChange('no')}
                          />
                        </Form.Group>
                        {isLinked && (
                          <Form.Group>
                            <Form.Label>Информация об организации</Form.Label>
                            <Form.Row>
                              <Col>
                                <Form.Control
                                  type="text"
                                  name="name"
                                  placeholder="Название"
                                  value={editingData.company?.name || ''}
                                  onChange={handleCompanyChange}
                                  className={styles.block_form}
                                />
                              </Col>
                              <Col>
                                <Form.Control
                                  type="text"
                                  name="last_name_of_responsible"
                                  placeholder="Фамилия ответственного"
                                  value={editingData.company?.last_name_of_responsible || ''}
                                  onChange={handleCompanyChange}
                                  className={styles.block_form}
                                />
                              </Col>
                              <Col>
                                <Form.Control
                                  type="text"
                                  name="first_name_of_responsible"
                                  placeholder="Имя ответственного"
                                  value={editingData.company?.first_name_of_responsible || ''}
                                  onChange={handleCompanyChange}
                                  className={styles.block_form}
                                />
                              </Col>
                              <Col>
                                <Form.Control
                                  type="text"
                                  name="patronymic_of_responsible"
                                  placeholder="Отчество ответственного"
                                  value={editingData.company?.patronymic_of_responsible || ''}
                                  onChange={handleCompanyChange}
                                  className={styles.block_form}
                                />
                              </Col>
                            </Form.Row>
                            <Form.Row>
                              <Col>
                                <Form.Control
                                  type="text"
                                  name="job_title_of_responsible"
                                  placeholder="Должность ответственного"
                                  value={editingData.company?.job_title_of_responsible || ''}
                                  onChange={handleCompanyChange}
                                  className={styles.block_form}
                                />
                              </Col>
                              <Col>
                                <Form.Control
                                  type="text"
                                  name="additional_information"
                                  placeholder="Дополнительная информация"
                                  value={editingData.company?.additional_information || ''}
                                  onChange={handleCompanyChange}
                                  className={styles.block_form}
                                />
                              </Col>
                            </Form.Row>
                          </Form.Group>
                        )}
                        <Form.Group>
                          <Form.Label>Ревьювер</Form.Label>
                          <Form.Row>
                            <Col>
                              <Form.Control
                                type="text"
                                name="last_name"
                                placeholder="Фамилия"
                                value={editingData.reviewer?.user?.last_name || ''}
                                onChange={handleReviewerChange}
                                className={styles.block_form}
                              />
                            </Col>
                            <Col>
                              <Form.Control
                                type="text"
                                name="first_name"
                                placeholder="Имя"
                                value={editingData.reviewer?.user?.first_name || ''}
                                onChange={handleReviewerChange}
                                className={styles.block_form}
                              />
                            </Col>
                            <Col>
                              <Form.Control
                                type="text"
                                name="patronymic"
                                placeholder="Отчество"
                                value={editingData.reviewer?.user?.patronymic || ''}
                                onChange={handleReviewerChange}
                                className={styles.block_form}
                              />
                            </Col>
                          </Form.Row>
                        </Form.Group>
                        <Button variant="primary" onClick={handleSave} className={styles.block_form}>
                          Сохранить
                        </Button>
                        <Button variant="secondary" onClick={handleCancel} className={styles.block_form}>
                          Отмена
                        </Button>
                      </Form>
                    </td>
                  </tr>
                )}
              </React.Fragment>
            ))}
          </tbody>
        </table>
      </div>
    
    </>
  );
};

export default EditThemesListSecretaryForm;
