import React, { Component } from 'react';
import { Button } from 'react-bootstrap';
import BaseModal from '../BaseModal/BaseModal';
import BlockInfo from '../../BlockInfo/BlockInfo';
import styles from './ModalRequests.module.css';
import RequestsContent from '../../ProfileBlocks/Teacher/RequestsCard/RequestsContent';

export default class ModalRequests extends Component {
  constructor(props) {
    super(props);
    this.state = {
      sortedRequests: this.props.requestdata,
      sortOrder: {}, // Добавляем состояние для хранения порядка сортировки по каждому критерию
    };
  }

  sortById = () => {
    const { sortedRequests, sortOrder } = this.state;
    const order = sortOrder['id'] === 'asc' ? 'desc' : 'asc'; // Проверяем текущий порядок сортировки и меняем его
    const sorted = [...sortedRequests].sort((a, b) => 
      order === 'asc' ? a.id - b.id : b.id - a.id // Сравниваем числа id
    );
    this.setState({ sortedRequests: sorted, sortOrder: { ...sortOrder, id: order } }); // Обновляем состояние с отсортированным списком и новым порядком сортировки
  };
  

  sortByAverageScore = () => {
    const { sortedRequests, sortOrder } = this.state;
    const order = sortOrder['name'] === 'asc' ? 'desc' : 'asc'; // Проверяем текущий порядок сортировки и меняем его
    const sorted = [...sortedRequests].sort((a, b) => 
      order === 'asc' ? a.student.surname.localeCompare(b.student.surname) : b.student.surname.localeCompare(a.student.surname)
    );
    this.setState({ sortedRequests: sorted, sortOrder: { ...sortOrder, name: order } }); // Обновляем состояние с отсортированным списком и новым порядком сортировки
  };

  render() {
    const { show, handleClose, darkMode, handleAccept, handleDecline } = this.props;
    const { sortedRequests } = this.state;

    const content = (
      <div>
        <BlockInfo>
          Вы можете в одностороннем порядке принять или отклонить заявки от выпускников.
          Если Вы принимаете заявку, то можете утвердить тему, поставив галочку в соответствующее окно. 
          Если Вы считаете, что тему нужно подкорректировать, оставьте поле чекбокса пустым.
        </BlockInfo>
        
        <div className="d-flex justify-content-center mb-3">
          <Button variant="primary" className="mx-2" onClick={this.sortById}>Сортировать по дате</Button>
          {/* Остальные кнопки сортировки */}
        </div>
        
        {sortedRequests.length === 0 ? (
          <p className="text-center">Вам еще не были отправлены заявки</p>
        ) : (
          <>
            {sortedRequests.map((request, index) => (
              <div className={`mx-5 ${styles.request_field}`} key={index}>
                <div className='mx-4'>
                  <RequestsContent
                    currentRequest={request}
                    handleAccept={() => handleAccept(index)}
                    handleDecline={() => handleDecline(index)}
                  />
                </div>
              </div>
            ))}
          </>
        )}
      </div>
    );

    return (
      <BaseModal 
        show={show}
        modalClass={styles.container_modal}
        handleClose={handleClose}
        title={'Текущие заявки'}
        content={content}
        darkMode={darkMode}
      />
    );
  }
}
