import React from 'react';

function ViewProtectionSchedulePersons({ protection_schedule, students_list_for_schedule }) {
    // Функция для форматирования даты
    const formatDate = (dateString) => {
        const parts = dateString.match(/(\d{2})(\d{2})(\d{4})/);
        if (parts && parts.length === 4) {
            const year = parts[3];
            const month = parseInt(parts[2], 10);
            const day = parseInt(parts[1], 10);
            const months = [
                'января', 'февраля', 'марта', 'апреля', 'мая', 'июня',
                'июля', 'августа', 'сентября', 'октября', 'ноября', 'декабря'
            ];
            return `${day} ${months[month - 1]} ${year}`;
        } else {
            return 'Invalid Date';
        }
    };

    const getFullName = (studentData) => {
        if (studentData && studentData.student) {
            const { last_name, first_name, patronymic } = studentData.student;
            return `${last_name} ${first_name} ${patronymic}`;
        } else {
            return 'Unknown';
        }
    };
    

    // Функция для получения группы по ID
    const getGroupNameById = (groupId) => {
        const group = protection_schedule.find(item => item.group?.id === groupId);
        return group ? group.group?.name : '';
    };

    // Группировка расписания по дням и группам
    const groupedSchedule = protection_schedule.reduce((acc, item) => {
        const dateKey = formatDate(item.date);
        if (!acc[dateKey]) {
            acc[dateKey] = {};
        }
        const groupName = getGroupNameById(item.group?.id);
        if (!acc[dateKey][groupName]) {
            acc[dateKey][groupName] = [];
        }
        const students = students_list_for_schedule.filter(student => student.student?.group?.id === item.group?.id);
        students.forEach(student => {
            const fullName = getFullName(student);
            acc[dateKey][groupName].push({ fullName, group: groupName, date: formatDate(item.date) });
        });
        return acc;
    }, {});

    // Функция для ограничения студентов до 12 человек в день для каждой группы
    const limitStudents = (schedule) => {
        Object.keys(schedule).forEach(date => {
            Object.keys(schedule[date]).forEach(group => {
                const students = schedule[date][group];
                if (students.length > 12) {
                    schedule[date][group] = students.slice(0, 12);
                }
            });
        });
        return schedule;
    };

    // Ограничиваем количество студентов до 12 человек в день для каждой группы
    const limitedSchedule = limitStudents(groupedSchedule);

    return (
        <div>
            {Object.entries(limitedSchedule).map(([date, groups]) => (
                <div key={date}>
                    <h3>{date}</h3>
                    <table>
                        <thead>
                            <tr>
                                <th>ФИО</th>
                                <th>Группа</th>
                                <th>Дата</th>
                            </tr>
                        </thead>
                        <tbody>
                            {Object.entries(groups).map(([group, students]) => (
                                students.map((student, index) => (
                                    <tr key={index}>
                                        <td>{student.fullName}</td>
                                        <td>{student.group}</td>
                                        <td>{student.date}</td>
                                    </tr>
                                ))
                            ))}
                        </tbody>
                    </table>
                </div>
            ))}
        </div>
    );
}

export default ViewProtectionSchedulePersons;
