import React from 'react';

function ViewProtectionSchedulePersons({ protection_schedule, students_list_for_schedule }) {
    // Функция для форматирования даты
    const formatDate = (dateString) => {
        const date = new Date(dateString);
        const options = { day: 'numeric', month: 'numeric', year: 'numeric' };
        return date.toLocaleDateString('ru-RU', options);
    };

    // Функция для получения полного имени студента
    const getFullName = (student) => {
        const { last_name, first_name, patronymic } = student.student;
        return `${last_name} ${first_name} ${patronymic}`;
    };

    // Функция для получения группы по ID
    const getGroupNameById = (groupId) => {
        const group = protection_schedule.find(item => item.group?.id === groupId);
        return group ? group.group?.name : '';
    };

    // Группировка расписания по дням и фамилиям
    const groupedSchedule = protection_schedule.reduce((acc, item) => {
        const dateKey = formatDate(item.date);
        if (!acc[dateKey]) {
            acc[dateKey] = {};
        }
        const student = students_list_for_schedule.filter(student => student.student?.group?.id === item.group?.id);
        const lastName = getFullName(student[0]);
        if (!acc[dateKey][lastName]) {
            acc[dateKey][lastName] = [];
        }
        acc[dateKey][lastName].push({ student: student[0], date: formatDate(item.date), group: getGroupNameById(item.group?.id) });
        return acc;
    }, {});

    return (
        <div>
            {Object.entries(groupedSchedule).map(([date, items]) => (
                <div key={date}>
                    <h3>{date}</h3>
                    {Object.entries(items).map(([fullName, students]) => (
                        <div key={fullName}>
                            <h4>{fullName}</h4>
                            <ul>
                                {students.map((student, index) => (
                                    <li key={index}>
                                        {student.date} - {student.group}
                                    </li>
                                ))}
                            </ul>
                        </div>
                    ))}
                </div>
            ))}
        </div>
    );
}

export default ViewProtectionSchedulePersons;
