import React, { Component, useState } from 'react';
import styles from './SideMenu.module.css';

const SideMenu = ({ toggleMenu, isSideMenuOpen }) => {
  // Добавляем вывод состояния меню в консоль
  console.log("Меню открыто:", isSideMenuOpen);

  return (
    <>
      <div className={styles.sidebar} style={{ display: isSideMenuOpen ? 'block' : 'none' }}>
        <button className="close-btn" onClick={() => { toggleMenu(); }}>×</button>
        <ul>
          <li><a href='profile_student'>Личный кабинет</a></li>
          <li><a href="view_protection_schedule"> График защит ВКР</a></li>
          <li><a href="#"> Инфо </a></li>
          <li><a href="#"> Приказы </a></li>
        </ul>
      </div>
      {isSideMenuOpen && <div className={styles.overlay} onClick={toggleMenu}></div>}
    </>
  );
};

export default SideMenu;
