import React, { useState } from 'react';
import { Form, Button, Row, Col, Modal } from 'react-bootstrap';
import plus from '../../../../img/plus.png';
import del from '../../../../img/del.png';
import edit from '../../../../img/edit.png';
import styles from '../Teacher/EditThemeList.module.css';

const EditThemesListSecretaryForm = ({ themesList: initialThemesList, data, teachers_list }) => {
  const [themesList, setThemesList] = useState(initialThemesList || []);
  const [editingIndex, setEditingIndex] = useState(null);
  const [editingData, setEditingData] = useState(null);
  const [editFormVisibleIndex, setEditFormVisibleIndex] = useState(null);
  const [isLinked, setIsLinked] = useState(false);
  const [filterAuthor, setFilterAuthor] = useState('');
  const [authorType, setAuthorType] = useState('');
  const [showAuthorModal, setShowAuthorModal] = useState(false);

  const addRow = () => {
    const newRow = {
      id: themesList.length ? Math.max(...themesList.map(item => item.id)) + 1 : 1,
      name: '',
      teacher: {
        user: data // Устанавливаем текущего пользователя в качестве преподавателя
      },
      company: {
        name: '',
        last_name_of_responsible: '',
        first_name_of_responsible: '',
        patronymic_of_responsible: '',
        job_title_of_responsible: '',
        additional_information: '',
      },
      free: true,
      fqw_by_application: '',
      reviewer: {
        user: {
          id: null,
          last_name: null,
          first_name: null,
          patronymic: null
        }
      },
      by_student: false,
      department: false,
    };
    const updatedThemesList = [newRow, ...themesList];
    setThemesList(updatedThemesList);
    setEditingIndex(newRow.id);
    setEditingData(newRow);
    setAuthorType('');
  };

  const handleSave = () => {
    const updatedThemesList = themesList.map(item =>
      item.id === editingIndex ? { ...item, ...editingData } : item
    );
    setThemesList(updatedThemesList);
    setEditFormVisibleIndex(null); // Устанавливаем null, чтобы скрыть форму редактирования
    setEditingIndex(null);
    setEditingData(null);
  };

  const handleFilterAuthorChange = (e) => {
    setFilterAuthor(e.target.value);
  };

  const filteredThemesList = themesList.filter(item => {
    // Если это новая строка (без автора), показываем ее в любом случае
    if (!item.teacher.user.first_name && !item.teacher.user.last_name && !item.teacher.user.patronymic && !item.by_student && !item.department) {
      return true;
    }

    if (item.teacher.user.first_name && item.teacher.user.last_name && item.teacher.user.patronymic) {
      return `${item.teacher.user.last_name} ${item.teacher.user.first_name} ${item.teacher.user.patronymic}`.toLowerCase().includes(filterAuthor.toLowerCase());
    } else if (item.by_student) {
      return 'предложено студентом'.toLowerCase().includes(filterAuthor.toLowerCase());
    } else if (item.department) {
      return 'кафедра сии'.toLowerCase().includes(filterAuthor.toLowerCase());
    } else {
      return true;
    }
  });

  const handleEditClick = (index) => {
    const themeToEdit = themesList.find(item => item.id === index);
    setEditingIndex(index);
    setEditingData(themeToEdit);
    setEditFormVisibleIndex(index);
    setIsLinked(!!themeToEdit.company.name);
    setAuthorType(
      themeToEdit.by_student ? 'student' :
      themeToEdit.department ? 'department' :
      'teacher'
    );
  };

  const handleCancel = () => {
    setEditFormVisibleIndex(null);
    if (editingIndex === null) {
      const newData = themesList.filter(item => item.id !== editingIndex);
      setThemesList(newData);
    }
    setEditingIndex(null);
  };

  const handleDeleteClick = (index) => {
    const newData = themesList.filter(item => item.id !== index);
    setThemesList(newData);
    if (editFormVisibleIndex === index) {
      setEditFormVisibleIndex(null);
    }
    setEditingIndex(null);
  };

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    const updatedEditingData = {
      ...editingData,
      [name]: type === 'checkbox' ? checked : value,
    };
    setEditingData(updatedEditingData);
  };

  const handleCompanyChange = (e) => {
    const { name, value } = e.target;
    const updatedEditingData = {
      ...editingData,
      company: {
        ...editingData.company,
        [name]: value,
      },
    };
    setEditingData(updatedEditingData);
  };

  const handleReviewerChange = (e) => {
    const { name, value } = e.target;
    const updatedReviewer = {
      ...editingData.reviewer.user,
      [name]: value,
    };
    const updatedEditingData = {
      ...editingData,
      reviewer: {
        user: updatedReviewer,
      },
    };
    setEditingData(updatedEditingData);
  };

  const handleRadioChange = (value) => {
    setIsLinked(value === 'yes');
    if (value === 'no') {
      // Если выбрана опция "Нет", очищаем поля организации
      const updatedEditingData = {
        ...editingData,
        company: {
          name: '',
          last_name_of_responsible: '',
          first_name_of_responsible: '',
          patronymic_of_responsible: '',
          job_title_of_responsible: '',
          additional_information: '',
        },
      };
      setEditingData(updatedEditingData);
    }
  };

  const handleAuthorTypeChange = (e) => {
    const { value } = e.target;
    setAuthorType(value);

    const updatedEditingData = {
      ...editingData,
      by_student: value === 'student',
      department: value === 'department',
      teacher: {
        user: value === 'teacher' ? editingData.teacher.user : { id: null, last_name: null, first_name: null, patronymic: null }
      }
    };

    setEditingData(updatedEditingData);
  };

  const handleTeacherSelectChange = (e) => {
    const teacherId = e.target.value;
    const selectedTeacher = teachers_list.find(teacher => teacher.user.id === parseInt(teacherId));

    const updatedEditingData = {
      ...editingData,
      teacher: {
        user: selectedTeacher.user
      }
    };

    setEditingData(updatedEditingData);
  };

  const openAuthorModal = () => setShowAuthorModal(true);
  const closeAuthorModal = () => setShowAuthorModal(false);

  const handleAuthorSave = () => {
    setEditingData({
      ...editingData,
      by_student: authorType === 'student',
      department: authorType === 'department',
      teacher: {
        user: authorType === 'teacher' ? editingData.teacher.user : { id: null, last_name: null, first_name: null, patronymic: null }
      }
    });
    closeAuthorModal();
  };

  return (
    <>
      <h4 className="mb-4 text-center">Редактирование списка тем ВКР</h4>
      <Form.Group as={Row} controlId="filterAuthor" className="justify-content-center">
        <Form.Label column sm={2} className="text-center my-3">Фильтр по автору</Form.Label>
        <Col sm={6}>
          <Form.Control
            type="text"
            value={filterAuthor}
            onChange={handleFilterAuthorChange}
            placeholder="Введите имя автора для фильтрации"
            className={styles.block_form}
          />
        </Col>
      </Form.Group>

      <div className={styles.catch}>
        <table className={styles.table_themes}>
          <thead className="text-center">
            <tr>
              <th rowSpan="2" width="10%">Автор</th>
              <th rowSpan="2" width="50%">Наименование темы ВКР</th>
              <th colSpan="4" className="no-filter">Организация</th>
              <th rowSpan="2" width="5%">ВКР по заявке</th>
              <th rowSpan="2" width="5%">Ревьювер</th>
              <th rowSpan="2">Занята</th>
              <th rowSpan="2" className="no-filter">Действия</th>
              <th className="no-filter">
                <img id="addRowButton" src={plus} onClick={addRow} width="30" height="30" alt="Add Row" />
              </th>
            </tr>
            <tr className="text-center">
              <th width="10%">Название</th>
              <th width="10%">ФИО</th>
              <th width="10%">Должность</th>
              <th width="15%">Доп. сведения</th>
            </tr>
          </thead>
          <tbody>
            {filteredThemesList.map((item, index) => (
              <React.Fragment key={index}>
                <tr className="text-center">
                  <td>
                    {item.by_student
                      ? 'Предложено студентом'
                      : item.department
                      ? 'Кафедра СИИ'
                      : `${item.teacher.user.last_name} ${item.teacher.user.first_name} ${item.teacher.user.patronymic}`}
                  </td>
                  <td>{item.name}</td>
                  <td>{item.company.name}</td>
                  <td>{`${item.company.last_name_of_responsible} ${item.company.first_name_of_responsible} ${item.company.patronymic_of_responsible}`}</td>
                  <td>{item.company.job_title_of_responsible}</td>
                  <td>{item.company.additional_information}</td>
                  <td>{item.fqw_by_application}</td>
                  <td>{`${item.reviewer.user.last_name || ''} ${item.reviewer.user.first_name || ''} ${item.reviewer.user.patronymic || ''}`}</td>
                  <td>{item.free ? 'Свободна' : 'Занята'}</td>
                  <td>
                    <div className={styles.actionIcons}>
                      <img
                        id="editRowButton"
                        src={edit}
                        width="30"
                        height="30"
                        alt="Edit"
                        onClick={() => handleEditClick(item.id)}
                      />
                      <img
                        id="deleteRowButton"
                        src={del}
                        width="30"
                        height="30"
                        alt="Delete"
                        onClick={() => handleDeleteClick(item.id)}
                      />
                    </div>
                  </td>
                </tr>
                {editFormVisibleIndex === item.id && (
                  <tr className={styles.editRow}>
                    <td colSpan="10">
                      <Form>
                        <Form.Group as={Row} controlId="name">
                          <Form.Label column sm={2}>Наименование темы</Form.Label>
                          <Col sm={10}>
                            <Form.Control
                              type="text"
                              name="name"
                              value={editingData.name}
                              onChange={handleInputChange}
                              placeholder="Введите наименование темы"
                            />
                          </Col>
                        </Form.Group>

                        <Form.Group as={Row} controlId="authorType">
                          <Form.Label column sm={2}>Автор</Form.Label>
                          <Col sm={10}>
                            <Form.Control as="select" value={authorType} onChange={handleAuthorTypeChange}>
                              <option value="">Выберите автора</option>
                              <option value="teacher">Преподаватель</option>
                              <option value="student">Студент</option>
                              <option value="department">Кафедра СИИ</option>
                            </Form.Control>
                          </Col>
                        </Form.Group>

                        {authorType === 'teacher' && (
                          <Form.Group as={Row} controlId="teacher">
                            <Form.Label column sm={2}>Преподаватель</Form.Label>
                            <Col sm={10}>
                              <Form.Control as="select" onChange={handleTeacherSelectChange}>
                                <option value="">Выберите преподавателя</option>
                                {teachers_list.map(teacher => (
                                  <option key={teacher.user.id} value={teacher.user.id}>
                                    {`${teacher.user.last_name} ${teacher.user.first_name} ${teacher.user.patronymic}`}
                                  </option>
                                ))}
                              </Form.Control>
                            </Col>
                          </Form.Group>
                        )}

                        <Form.Group as={Row} controlId="isLinked">
                          <Form.Label column sm={2}>Связано с организацией</Form.Label>
                          <Col sm={10}>
                            <Form.Check
                              inline
                              type="radio"
                              label="Да"
                              name="isLinked"
                              value="yes"
                              checked={isLinked}
                              onChange={() => handleRadioChange('yes')}
                            />
                            <Form.Check
                              inline
                              type="radio"
                              label="Нет"
                              name="isLinked"
                              value="no"
                              checked={!isLinked}
                              onChange={() => handleRadioChange('no')}
                            />
                          </Col>
                        </Form.Group>

                        {isLinked && (
                          <>
                            <Form.Group as={Row} controlId="companyName">
                              <Form.Label column sm={2}>Название организации</Form.Label>
                              <Col sm={10}>
                                <Form.Control
                                  type="text"
                                  name="name"
                                  value={editingData.company.name}
                                  onChange={handleCompanyChange}
                                  placeholder="Введите название организации"
                                />
                              </Col>
                            </Form.Group>

                            <Form.Group as={Row} controlId="last_name_of_responsible">
                              <Form.Label column sm={2}>Фамилия ответственного</Form.Label>
                              <Col sm={10}>
                                <Form.Control
                                  type="text"
                                  name="last_name_of_responsible"
                                  value={editingData.company.last_name_of_responsible}
                                  onChange={handleCompanyChange}
                                  placeholder="Введите фамилию ответственного"
                                />
                              </Col>
                            </Form.Group>

                            <Form.Group as={Row} controlId="first_name_of_responsible">
                              <Form.Label column sm={2}>Имя ответственного</Form.Label>
                              <Col sm={10}>
                                <Form.Control
                                  type="text"
                                  name="first_name_of_responsible"
                                  value={editingData.company.first_name_of_responsible}
                                  onChange={handleCompanyChange}
                                  placeholder="Введите имя ответственного"
                                />
                              </Col>
                            </Form.Group>

                            <Form.Group as={Row} controlId="patronymic_of_responsible">
                              <Form.Label column sm={2}>Отчество ответственного</Form.Label>
                              <Col sm={10}>
                                <Form.Control
                                  type="text"
                                  name="patronymic_of_responsible"
                                  value={editingData.company.patronymic_of_responsible}
                                  onChange={handleCompanyChange}
                                  placeholder="Введите отчество ответственного"
                                />
                              </Col>
                            </Form.Group>

                            <Form.Group as={Row} controlId="job_title_of_responsible">
                              <Form.Label column sm={2}>Должность ответственного</Form.Label>
                              <Col sm={10}>
                                <Form.Control
                                  type="text"
                                  name="job_title_of_responsible"
                                  value={editingData.company.job_title_of_responsible}
                                  onChange={handleCompanyChange}
                                  placeholder="Введите должность ответственного"
                                />
                              </Col>
                            </Form.Group>

                            <Form.Group as={Row} controlId="additional_information">
                              <Form.Label column sm={2}>Доп. информация</Form.Label>
                              <Col sm={10}>
                                <Form.Control
                                  as="textarea"
                                  name="additional_information"
                                  value={editingData.company.additional_information}
                                  onChange={handleCompanyChange}
                                  placeholder="Введите дополнительную информацию"
                                />
                              </Col>
                            </Form.Group>
                          </>
                        )}

                        <Form.Group as={Row} controlId="fqw_by_application">
                          <Form.Label column sm={2}>ВКР по заявке</Form.Label>
                          <Col sm={10}>
                            <Form.Control
                              type="text"
                              name="fqw_by_application"
                              value={editingData.fqw_by_application}
                              onChange={handleInputChange}
                              placeholder="Введите информацию о ВКР по заявке"
                            />
                          </Col>
                        </Form.Group>

                        <Form.Group as={Row} controlId="reviewer">
                          <Form.Label column sm={2}>Ревьювер</Form.Label>
                          <Col sm={10}>
                            <Form.Control
                              type="text"
                              name="last_name"
                              value={editingData.reviewer.user.last_name || ''}
                              onChange={handleReviewerChange}
                              placeholder="Фамилия"
                            />
                            <Form.Control
                              type="text"
                              name="first_name"
                              value={editingData.reviewer.user.first_name || ''}
                              onChange={handleReviewerChange}
                              placeholder="Имя"
                            />
                            <Form.Control
                              type="text"
                              name="patronymic"
                              value={editingData.reviewer.user.patronymic || ''}
                              onChange={handleReviewerChange}
                              placeholder="Отчество"
                            />
                          </Col>
                        </Form.Group>

                        <Form.Group as={Row} controlId="free">
                          <Form.Label column sm={2}>Занята</Form.Label>
                          <Col sm={10}>
                            <Form.Check
                              type="checkbox"
                              name="free"
                              checked={!editingData.free}
                              onChange={handleInputChange}
                              label="Занята"
                            />
                          </Col>
                        </Form.Group>

                        <div className="text-center">
                          <Button variant="success" onClick={handleSave}>Сохранить</Button>
                          <Button variant="secondary" onClick={handleCancel} className="ml-2">Отмена</Button>
                        </div>
                      </Form>
                    </td>
                  </tr>
                )}
              </React.Fragment>
            ))}
          </tbody>
        </table>
      </div>
      
      <Modal show={showAuthorModal} onHide={handleAuthorModalClose}>
        <Modal.Header closeButton>
          <Modal.Title>Выбор автора</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="authorType">
              <Form.Label>Выберите автора</Form.Label>
              <Form.Control as="select" value={authorType} onChange={handleAuthorTypeChange}>
                <option value="">Выберите автора</option>
                <option value="teacher">Преподаватель</option>
                <option value="student">Студент</option>
                <option value="department">Кафедра СИИ</option>
              </Form.Control>
            </Form.Group>
            <Button variant="primary" onClick={closeAuthorModal } className="mt-3">Выбрать</Button>
          </Form>
        </Modal.Body>
      </Modal>

   </>
);
};


export default EditThemesListSecretaryForm;
