import React, { useState } from 'react';
import { Container, Row, Col, Form, Button } from 'react-bootstrap';
import styles from '../../ProfileBlocks.module.css';
import reveal from '../../../../img/reveal.png';
import ModalRequests from '../../../Modals/ModalRequests/ModalRequests';
import RequestsContent from './RequestsContent';

const RequestsCard = ({ requestdata }) => {
  const [currentRequestIndex, setCurrentRequestIndex] = useState(0); // Состояние для отслеживания текущей заявки
  const [requestsCount, setRequestsCount] = useState(requestdata.length); // Состояние для подсчета количества заявок
  const [showModal, setShowModal] = useState(false); // Состояние для показа модального окна

  const handleAccept = () => {
    setCurrentRequestIndex(prevIndex => prevIndex + 1); // Увеличиваем индекс текущей заявки
    setRequestsCount(prevCount => prevCount - 1); // Уменьшаем количество заявок
  };

  const handleDecline = () => {
    setCurrentRequestIndex(prevIndex => prevIndex + 1); // Увеличиваем индекс текущей заявки
    setRequestsCount(prevCount => prevCount - 1); // Уменьшаем количество заявок
  };

  const currentRequest = requestdata[currentRequestIndex]; // Получаем текущую заявку на основе текущего индекса
  const noRequestsLeft = !currentRequest || currentRequestIndex >= requestdata.length; // Проверяем, есть ли еще заявки для отображения

  const showNextRequest = () => {
    if (currentRequestIndex < requestdata.length - 1) {
      setCurrentRequestIndex(prevIndex => prevIndex + 1); // Если есть еще заявки, переходим к следующей
    }
  };

  const handleCloseModal = () => {
    setShowModal(false); // Закрываем модальное окно
    showNextRequest(); // Показываем следующую заявку
  };

  const visibleRequests = requestdata.filter(request => !request.hidden); // Фильтруем видимые заявки

  return (
    <div className={styles.card}>
      <p className="text-center px-1 py-2 fw-bold">Текущие заявки <span className={styles.badge}>{requestsCount}</span>
        <img src={reveal} className="mx-3" width="20" height="20" onClick={() => setShowModal(true)} alt="reveal" />
      </p>
      {noRequestsLeft ? (
        <p className="text-center">Вам еще не были отправлены заявки</p>
      ) : (
        <RequestsContent
          currentRequest={currentRequest}
          handleAccept={handleAccept}
          handleDecline={handleDecline}
        />
      )}
      <ModalRequests
        show={showModal}
        onHide={handleCloseModal}
        requestdata={requestdata}
        sourceData={visibleRequests}
      />
    </div>
  );
}

export default RequestsCard;
