import React, { useState } from 'react';
import BlockInfo from '../../BlockInfo/BlockInfo';
import plus from '../../../img/plus.png';
import del from '../../../img/del.png';
import edit from '../../../img/edit.png';
import styles from '../EditThemeList/EditThemeList.module.css';
import EditTheme from '../../Forms/EditTheme/EditTheme';

const EditThemeListTeacherForm = ({ themesList: initialThemesList, data }) => {
  const [themesList, setThemesList] = useState(initialThemesList || []);
  const [editingIndex, setEditingIndex] = useState(null);
  const [editingData, setEditingData] = useState(null);
  const [editFormVisible, setEditFormVisible] = useState(false);

  const addRow = () => {
    const newRow = {
      name: '',
      company: { name: '', last_name_of_responsible: '', first_name_of_responsible: '', job_title_of_responsible: '', additional_information: '' },
      free: null,
    };
    setThemesList([...themesList, newRow]);
  };

  const handleEditClick = (index) => {
    setEditingIndex(index);
    setEditingData(themesList[index]);
    setEditFormVisible(true); // Показываем форму при нажатии на кнопку редактирования
  };

  const handleCancel = () => {
    setEditFormVisible(false); // Скрываем форму при нажатии на кнопку отмены
  };

  return (
    <>
      <h4 className="mb-4 text-center">Редактирование списка тем ВКР</h4>
      <BlockInfo>
        Вы можете редактировать уже созданные темы ВКР и добавлять новые. С помощью фильтрации можете выбрать нужные темы: по названию, наличию организации, а также свободные и занятые темы.
      </BlockInfo>

      <table width="100%" className={styles.table_themes}>
        {/* Код таблицы */}
      </table>

      {/* Форма редактирования */}
      {editFormVisible && (
        <form id="editForm" className="py-2 my-2 mx-5">
          {/* Код формы */}
        </form>
      )}
    </>
  );
}

export default EditThemeListTeacherForm;
