import React, {Component} from 'react'
import BasePage from '../BasePages/BasePage.js';
import Search from '../../components/ProfileBlocks/Search/Search.js'
import ProfileBlocksSecretary from '../../components/ProfileBlocks/Secretary/ProfileBlocksSecretary/ProfileBlocksSecretary.jsx'
import axios from 'axios'

class ProfileSecretary extends Component {
  static defaultProps = {
    themes_list: [] // Provide an empty array as default value
  };

  state = {
    details: null,
    themes_list: this.props.themes_list
  };

  componentDidMount() {
    const token = localStorage.getItem('token'); // Получаем токен из локального хранилища
    if (token) {
      axios.get('http://127.0.0.1:8000/v1/main/secretary/profile/', {
        headers: {
          Authorization: `Token ${token}` // Устанавливаем заголовок авторизации с токеном
        }
      })
      .then(res => {
        this.setState({
          details: res.data // Устанавливаем полученные данные в состояние
        });
      })
      .catch(err => {
        console.log(err); // Обработка ошибки
      });
    }
  }

  render() {
    const { details } = this.state;
    return (
      <BasePage isAuthenticated={true} data={details}>
        <Search />
        <ProfileBlocksSecretary secretaryData={details}/>
      </BasePage>
    );
  }
}

export default ProfileSecretary;
