import React, { useState, useMemo, useEffect } from 'react';
import { useTable, useFilters } from 'react-table';
import * as XLSX from 'xlsx';
import { saveAs } from 'file-saver';
import { Button } from 'react-bootstrap';
import styles from '../../../container.module.css';
import Filter from '../Filter/Filter';
import hide_cell from '../../../img/hide_cell.png';

function SecretaryHugeTable({ info_for_table }) {
  const [tableData, setTableData] = useState(info_for_table.requests);
  const [originalData] = useState(info_for_table.requests); // Store the original data
  const [editableCell, setEditableCell] = useState(null);
  const [hiddenColumns, setHiddenColumns] = useState([]);
  const columns = React.useMemo(
    () => [
      
      {
        Header: '№ п/п',
        accessor: (row, index) => index + 1,
        Filter: Filter
      },
      {
        Header: 'ср.б.',
        accessor: (row) =>  `${row.student.average_score}`,
        Filter: Filter
      },
      {
        Header: 'Фамилия, Имя, Отчество',
        accessor: (row) => `${row.student.surname} ${row.student.name} ${row.student.patronymic}`,
        Filter: Filter,
      },
      {
        Header: 'Тема',
        
        accessor:  (row) =>  `${row.theme.name}`,
        Filter: Filter,
      },
      {
        Header: 'Руководитель',
        accessor: (row) => `${row.teacher.surname} ${row.teacher.name} ${row.teacher.patronymic}`,
        Filter: Filter,
      },
      {
        Header: 'Консультант',
        accessor: (row) => `${row.consultant.surname} ${row.consultant.name} ${row.consultant.patronymic}`,
        Filter: Filter,
      },
      {
        Header: 'Рецензент',
        accessor: (row) =>  `${row.consultant.surname} ${row.consultant.name} ${row.consultant.patronymic}`,
        Filter: Filter,
      },
      {
        Header: 'организация',
        accessor: (row) =>  `${row.theme.organization.name} ${row.theme.organization.last_name_of_responsible} ${row.theme.organization.first_name_of_responsible} ${row.theme.organization.patronymic_name_of_responsible}
         ${row.theme.organization.job_title_of_resposible} ${row.theme.organization.additional_information}`,
         Filter: Filter,
      },
      {
        Header: 'Телефон',
        accessor: (row) =>  `${row.student.phone}`,
        Filter: Filter,
      },
      {
        Header: 'Email',
        accessor: (row) =>  `${row.student.email}`,
        Filter: Filter,
      },
      {
        Header: 'бюджет/платный',
        accessor: (row) =>  `${row.student.basis_of_study}`,
        Filter: Filter,
      },
      {
        Header: 'оценка на предзащите/ преддипломная практика',
        accessor: (row) =>  `${row.preprotection_grade}`,
        Filter: Filter,
      },
      {
        Header: 'оценка на защите',
        accessor: (row) =>  `${row.workProtecton.final_grade}`,
        Filter: Filter,
      },
      {
        Header: 'дата рождения',
        accessor: (row) =>  `${row.student.date_of_birth}`,
        Filter: Filter,
      },
      {
        Header: 'год окончания предыдущ образования',
        accessor: (row) =>  `${row.student.year_of_prev_education_completion}`,
        Filter: Filter,
      },
      {
        Header: 'форма предыдущего образования',
        accessor: (row) =>  `${row.student.form_of_prev_education}`,
        Filter: Filter,
      },
      {
        Header: 'год поступления',
        accessor: (row) =>  `${row.student.year_of_admission}`,
        Filter: Filter,
      },
      {
        Header: 'особые отметки',
        accessor: (row) =>  `${row.student.special_marks}`,
        Filter: Filter,
      },
      {
        Header: 'вид ВКР',
        accessor: (row) =>  `${row.type_of_fqw}`,
        Filter: Filter,
        headerProps: { rowSpan: 2 },
      },
      {
        Header: 'ВКР по заявке',
        accessor: (row) =>  `${row.theme.fqw_by_application}`,
        Filter: Filter,
      },
      {
        Header: 'ВКР на англ. языке',
        accessor: (row) => (row.fqw_in_english ? 'да' : 'нет'),
        Filter: Filter,
      },
      {
        Header: 'ВКР рекомендовано',
        accessor: (row) =>  `${row.fqw_recommended}`,
        Filter: Filter,
      },
      {
        Header: 'наличие спец. условий',
        accessor: (row) => (row.special_conditions ? 'да' : 'нет'),
        Filter: Filter,
      },
      {
        Header: 'приказы',
        columns: [
          {
            Header: 'допуск',
            accessor: (row) => `${row.workProtecton.final_grade}`,
            Filter: Filter,
            isVisible: true
          },
          {
            Header: 'тема',
            accessor: (row) => `${row.workProtecton.final_grade}`,
            Filter: Filter,
            isVisible: true
          },
          {
            Header: 'изменение темы',
            accessor: (row) => `${row.workProtecton.final_grade}`,
            Filter: Filter,
            isVisible: true
          },
          {
            Header: 'рецензент',
            accessor: (row) => `${row.workProtecton.final_grade}`,
            Filter: Filter,
            isVisible: true
          },
          {
            Header: 'отчисление',
            accessor: (row) => `${row.workProtecton.final_grade}`,
            Filter: Filter,
            isVisible: true
          },
          {
            Header: 'с какого числа отчисление',
            accessor: (row) => `${row.workProtecton.final_grade}`,
            Filter: Filter,
            isVisible: true
          }
        ]
      },
      {
        Header: 'отметки о выполнении',
        columns: [
          
      {
        Header: 'долги',
        accessor: (row) =>  `${row.student.number_of_debts}`,
        Filter: Filter
      },
      {
        Header: 'справка о выполнении уч. плана',
        accessor: (row) =>  `${row.student.certificate_of_curriculum_completion}`,
        Filter: Filter
      },
      {
        Header: 'сдана зачетка',
        accessor: (row) =>  `${row.student.record_book_submitted}`,
        Filter: Filter
      },
      {
        Header: 'сдана пояснительная записка',
        accessor: (row) => (row.explanatory_note_submitted ? 'да' : 'нет'),
        Filter: Filter
      },
      {
        Header: 'красный диплом',
        accessor: (row) => (row.student.diploma_with_honors ? 'да' : 'нет'),
        Filter: Filter
      },
      {
        Header: 'согласие на публикацию',
        accessor: (row) => (row.publication_agree ? 'да' : 'нет'),
        Filter: Filter
      },
      {
        Header: 'отзыв',
        accessor: (row) =>  `${row.teacher_review}`,
        Filter: Filter
      },
      {
        Header: 'рецензия',
        accessor: (row) =>  `${row.review}`,
        Filter: Filter
      },
      {
        Header: 'Акт о внедрении',
        accessor: (row) => (row.implementation_act ? 'да' : 'нет'),
        Filter: Filter
      },
      {
        Header: 'отчет о плагиате',
        accessor: (row) => (row.plagarism_report ? row.plagarism_report : 'нет'),
        Filter: Filter
      },
      {
        Header: 'заявление на последипломный отпуск',
        accessor: (row) => (row.student.application_for_postgraduate_leave ? row.student.application_for_postgraduate_leave: 'нет'),
        Filter: Filter
      },
      {
        Header: 'рекомендован к поступлению',
        accessor: (row) =>  `${row.student.recommended_for_admission}`,
        Filter: Filter
      },
        ]
      },
    ],
    []
  );

  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow,
    allColumns,
    toggleHideColumn,
  } = useTable(
    { columns, data: tableData },
    useFilters // Use the useFilters plugin hook
  );

  const handleDoubleClick = (rowIndex, columnId) => {
    setEditableCell({ rowIndex, columnId });
  };

  const handleCellChange = (e, rowIndex, columnId) => {
    const newData = [...tableData];
    newData[rowIndex][columnId] = e.target.value;
    setTableData(newData);
  };

  const toggleColumnVisibility = (columnId) => {
    const newHiddenColumns = hiddenColumns.includes(columnId)
      ? hiddenColumns.filter((id) => id !== columnId)
      : [...hiddenColumns, columnId];
    setHiddenColumns(newHiddenColumns);
    toggleHideColumn(columnId);
  };

  useEffect(() => {
    // Apply hidden columns when component mounts
    setHiddenColumns([]);
  }, []);

  const exportToExcel = () => {
    const tableClone = document.getElementById('dataTable').cloneNode(true);
    const hiddenCells = tableClone.querySelectorAll(`.${styles.hidden}`);
    hiddenCells.forEach((cell) => cell.remove());

    const ws = XLSX.utils.table_to_sheet(tableClone);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, 'Data');
    XLSX.writeFile(wb, 'Data.xlsx');
  };

  const resetTable = () => {
    setTableData(originalData);
    setHiddenColumns([]);
    setEditableCell(null);
    setIsColumnsModified(false); // Reset the modification state
    // Restore hidden columns
    hiddenColumns.forEach((columnId) => {
      toggleHideColumn(columnId);
    });
  };

  const showOnlyColumnsByIndex = (columnIndexesToShow) => {
    if (isColumnsModified) return; // Do nothing if columns have been modified

    const newHiddenColumns = allColumns.map((column, index) =>
      columnIndexesToShow.includes(index) ? '' : column.id
    ).filter(columnId => columnId !== '');
    setHiddenColumns(newHiddenColumns);
    newHiddenColumns.forEach((columnId) => {
      toggleHideColumn(columnId);
    });
    setIsColumnsModified(true); // Set modification state
  };

  return (
    <div>
      <h4 className="mb-4 text-center">Таблица данных</h4>
      <Button onClick={exportToExcel} className="mb-4">
        Export to Excel
      </Button>
      <Button onClick={resetTable} className="mb-4">
        Сбросить таблицу
      </Button>
      <Button onClick={() => showOnlyColumnsByIndex([0, 1, 2, 3, 4, 5])} className="mb-4">
        Оставить только определенные столбцы
      </Button>

      <table {...getTableProps()} id="dataTable" className={styles.huge_table}>
        <thead>
          {headerGroups.map(headerGroup => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map(column => (
                <th
                  {...column.getHeaderProps()}
                  style={{
                    display: hiddenColumns.includes(column.id) ? 'none' : '',
                    position: 'relative',
                    paddingRight: '25px',
                  }}
                >
                  {column.render('Header')}
                  {column.canFilter && (
                    <div
                      style={{
                        position: 'absolute',
                        right: '0',
                        bottom: '0',
                        marginRight: '-13%',
                        marginBottom: '-8%'
                      }}
                    >
                      <Filter column={column} />
                    </div>
                  )}
                  {column.canFilter && (
                    <div
                      style={{
                        position: 'absolute',
                        right: '0',
                        top: '0',
                        marginRight: '-13%',
                        marginTop: '-10%'
                      }}
                    >
                      <Button variant="none" onClick={() => toggleColumnVisibility(column.id)}>
                        <img src={hide_cell} width={25} height={25} alt="Скрыть" />
                      </Button>
                    </div>
                  )}
                </th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {rows.map((row, rowIndex) => {
            prepareRow(row);
            return (
              <tr {...row.getRowProps()}>
                {row.cells.map((cell) => (
                  <td
                    {...cell.getCellProps()}
                    className={`${styles.data_cell} ${hiddenColumns.includes(cell.column.id) ? styles.hidden : ''}`}
                    style={{ 
                      display: hiddenColumns.includes(cell.column.id) ? 'none' : '',
                      width: cell.column.width // Keep the cell width consistent
                    }}
                    onDoubleClick={() => handleDoubleClick(rowIndex, cell.column.id)}
                  >
                    {editableCell && editableCell.rowIndex === rowIndex && editableCell.columnId === cell.column.id ? (
                      <input
                        type="text"
                        value={cell.value}
                        onChange={(e) => handleCellChange(e, rowIndex, cell.column.id)}
                      />
                    ) : (
                      cell.render('Cell')
                    )}
                  </td>
                ))}
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

export default SecretaryHugeTable;
