import React, { useState } from 'react';
import styles from "./GenerateApplicationForm.module.css";
import styles_base from "../../Forms/FormsBase.module.css";
import BlockInfo from '../../BlockInfo/BlockInfo';
import { Form, Button, Col, Row } from 'react-bootstrap';
import { saveAs } from 'file-saver';
import PizZip from 'pizzip';
import Docxtemplater from 'docxtemplater';
import word from './1.docx'
import petrovich from 'petrovich'
import RussianNouns from 'russian-nouns-js'

const направления = {
  "09.03.02-Информационные системы и технологии": "09.03.02.30-Информационные системы и технологии",
  "09.04.01-Информатика и вычислительная техника": "09.04.01.10-Интеллектуальные информационные системы",
  "09.04.02-Информационные системы и технологии": [
    "09.04.02.01-Информационно-управляющие системы",
    "09.04.02.05-Информационные системы дистанционного зондирования Земли"
  ]
};

function GenerateApplicationForm({studentData, teacherData, requestData}) {
    const [selectedKey, setSelectedKey] = useState('');
    const [selectedValue, setSelectedValue] = useState('');
    const [showFields, setShowFields] = useState(false);
  
    const handleKeyChange = (event) => {
        const key = event.target.value;
        setSelectedKey(key);
        setSelectedValue(направления[key] || '');
      };
    
      const handleValueChange = (event) => {
        const value = event.target.value;
        setSelectedValue(value);
        const key = Object.keys(направления).find((k) => {
          const val = направления[k];
          if (Array.isArray(val)) {
            return val.includes(value);
          }
          return val === value;
        });
        setSelectedKey(key || '');
      };
      
  
    const handleRadioChange = (e) => {
      setShowFields(e.target.value === 'option1');
    };
    const fullname = studentData.surname + ' ' + studentData.name + ' ' + studentData.patronymic
    const fullnameTeacher = teacherData.surname + ' ' + teacherData.name + ' ' + teacherData.patronymic

    const np_s = studentData.name.charAt(0) + '.'+ studentData.patronymic.charAt(0) + '. ';
    const np_t = teacherData.name.charAt(0) + '.' + teacherData.patronymic.charAt(0) + '. ';

    var surname_gen = petrovich.female.last.genitive (studentData.surname);
    var teacher = {
      last: teacherData.surname,
      first: teacherData.name,
      middle: teacherData.patronymic
     };

     var fullnameTeac = petrovich(teacher, 'accusative');
     fullnameTeac = fullnameTeac.last + ' ' + fullnameTeac.first + ' '+ fullnameTeac.middle

     const rne = new RussianNouns.Engine();
    
     let academic_title = teacherData.academic_title;
     let academic_degree = teacherData.academic_degree;
     const firstWordTitle = academic_title.split(" ")[0];
     const firstWordDegree = academic_degree.split(" ")[0];
     const declinedTitle = rne.decline({text: firstWordTitle, gender: 'средний'}, 'родительный');
     const declinedDegree = rne.decline({text: firstWordDegree, gender: 'средний'}, 'родительный');
     academic_title = academic_title.replace(firstWordTitle, declinedTitle);
     academic_degree = academic_degree.replace(firstWordDegree, declinedDegree);

    function extractVariables(template) {
      var re = /({.*?})/sg;
      let variables = [];
      let result;
      while ((result = re.exec(template)) !== null) {
          variables.push(result[0]); 
      }
      return variables;
  }
  
  const generateDocument = () => {
    const xhr = new XMLHttpRequest();
    xhr.open('GET', word, true);
    xhr.responseType = 'arraybuffer';

    xhr.onload = () => {
      if (xhr.status === 200) {
        const data = new Uint8Array(xhr.response);
        const zip = new PizZip(data);
        let doc;
        try {
          doc = new Docxtemplater(zip);
        } catch (error) {
          console.error('Ошибка создания документа:', error);
          return;
        }

        const dataToRender = {
          np_s: np_s,
          np_t: np_t,
          surname: surname_gen,
          phoneNumber: studentData.phone_number,
          email: studentData.email,
          kurs: studentData.kurs,
          group: studentData.group,
          specialtyCode: studentData.specialtyCode,
          specialtyName: studentData.specialtyName,
          fieldOfStudyCode: studentData.fieldOfStudyCode,
          fieldOfStudyName: studentData.fieldOfStudyName,
          form_of_studying: studentData.form_of_studying,
          base_of_studying: studentData.basis_of_study,
          fullname: fullname,
          theme_str: requestData.theme_str,
          fullnameTeacher:fullnameTeac,
          surnameTeacher: teacherData.surname,
          academic_degree: academic_degree,
          academic_title: academic_title,
          job_title: teacherData.job_title,
          place_of_work: teacherData.place_of_work,
        };

        doc.setData(dataToRender);

        try {
          doc.render();
        } catch (error) {
          console.error('Ошибка рендеринга документа:', error);
          return;
        }

        const output = doc.getZip().generate({ type: 'blob' });
        saveAs(output, 'generated_document.docx');
      } else {
        console.error('Ошибка загрузки файла:', xhr.statusText);
      }
    };

    xhr.onerror = () => {
      console.error('Ошибка загрузки файла:', xhr.statusText);
    };

    xhr.send();
  };



