import petrovich from 'petrovich';
import RussianNouns from 'russian-nouns-js';
import { saveAs } from 'file-saver';
import PizZip from 'pizzip';
import Docxtemplater from 'docxtemplater';
import word from './1.docx';

const generateDocument = async ({ generating_info, action }) => {

  if (action === 'approve') action = 'утвердить';
  if (action === 'change') action = 'изменить';


  let type_of_program, type_of_speciality;
  if (generating_info.student.level_of_education === 'бакалавриат') {
    type_of_program = 'профилю / специализации';
    type_of_speciality = 'подготовки / специальности';
  } else if (generating_info.student.level_of_education === 'магистратура') {
    type_of_program = 'магистерской программе';
    type_of_speciality = 'подготовки';
  }

  // Формирование полных имен
  const fullname = `${generating_info.student.user.last_name} ${generating_info.student.user.first_name} ${generating_info.student.user.patronymic}`;
  const np_s = `${generating_info.student.user.first_name.charAt(0)}.${generating_info.student.user.patronymic.charAt(0)}. `;
  const np_t = `${generating_info.teacher.user.first_name.charAt(0)}.${generating_info.teacher.user.patronymic.charAt(0)}. `;

  // Склонение фамилии студента
  const surname_gen = petrovich.female.last.genitive(generating_info.student.user.last_name);
  const teacher = {
    last: generating_info.teacher.user.last_name,
    first: generating_info.teacher.user.first_name,
    middle: generating_info.teacher.user.patronymic,
  };

  let fullnameTeac = petrovich(teacher, 'accusative');
  fullnameTeac = `${fullnameTeac.last} ${fullnameTeac.first} ${fullnameTeac.middle}`;

  const rne = new RussianNouns.Engine();

  // Склонение различных атрибутов
  const declineWord = (word, gender) => rne.decline({ text: word.split(' ')[0], gender }, 'родительный');
  let form_of_studying = generating_info.student.form_of_education.replace(generating_info.student.form_of_education.split(' ')[0], declineWord(generating_info.student.form_of_education, 'женский'));
  let academic_title = generating_info.teacher.employee_information.academic_title.replace(generating_info.teacher.employee_information.academic_title.split(' ')[0], declineWord(generating_info.teacher.employee_information.academic_title, 'средний'));
  let academic_degree = generating_info.teacher.employee_information.academic_degree.replace(generating_info.teacher.employee_information.academic_degree.split(' ')[0], declineWord(generating_info.teacher.employee_information.academic_degree, 'средний'));
  let job_title = generating_info.teacher.employee_information.job_title.replace(generating_info.teacher.employee_information.job_title.split(' ')[0], declineWord(generating_info.teacher.employee_information.job_title, 'средний'));
  let place_of_work = generating_info.teacher.employee_information.place_of_work.replace(generating_info.teacher.employee_information.place_of_work.split(' ')[0], declineWord(generating_info.teacher.employee_information.place_of_work, 'женский'));

  try {
    // Загрузка шаблона документа
    const response = await fetch(word);
    if (!response.ok) throw new Error('Ошибка сети');
    const arrayBuffer = await response.arrayBuffer();
    const zip = new PizZip(arrayBuffer);
    const doc = new Docxtemplater(zip);

    // Данные для рендеринга
    const dataToRender = {
      action,
      type_of_program,
      type_of_speciality,
      np_s,
      np_t,
      surname: generating_info.student.user.last_name,
      surname_gen,
      phoneNumber: generating_info.student.user.phone,
      email: generating_info.student.user.email,
      kurs: generating_info.student.group.course_number,
      group: generating_info.student.group.name,
      specialtyCode: generating_info.student.group.speciality.code,
      specialtyName: generating_info.student.group.speciality.name,
      fieldOfStudyCode: generating_info.student.group.speciality.field_of_study.code,
      fieldOfStudyName: generating_info.student.group.speciality.field_of_study.name,
      form_of_studying,
      base_of_studying: generating_info.student.basis_of_study,
      fullname,
      theme_str: generating_info.theme.name,
      fullnameTeacher: fullnameTeac,
      surnameTeacher: generating_info.teacher.user.last_name,
      academic_degree,
      academic_title,
      job_title,
      place_of_work,
    };

    // Установка данных и рендеринг документа
    doc.setData(dataToRender);
    try {
      doc.render();
    } catch (error) {
      console.error('Ошибка рендеринга документа:', error);
      return;
    }

    // Сохранение документа
    const output = doc.getZip().generate({ type: 'blob' });
    saveAs(output, 'Заявление.docx');
  } catch (error) {
    console.error('Ошибка загрузки файла:', error);
  }
};

export default generateDocument;

function extractVariables(template) {
  const re = /({.*?})/sg;
  const variables = [];
  let result;
  while ((result = re.exec(template)) !== null) {
    variables.push(result[0]);
  }
  return variables;
}
