import React, { useState } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
import { useAsyncDebounce } from 'react-table';
import filter from '../../../img/filter.png'

const Filter = ({ column: { filterValue, setFilter, preFilteredRows, id } }) => {
    const [isOpen, setIsOpen] = useState(false);
    const [buttonCoordinates, setButtonCoordinates] = useState({ x: 0, y: 0 });
  
    const toggleModal = () => {
      setIsOpen(!isOpen);
    };
  
    const applyFilter = (value) => {
      setFilter(value || undefined);
      toggleModal();
    };
  
    const options = React.useMemo(() => {
      const options = new Set();
      preFilteredRows.forEach(row => {
        options.add(row.values[id]);
      });
      return [...options.values()];
    }, [id, preFilteredRows]);
  
    const onChange = useAsyncDebounce(value => {
      applyFilter(value);
    }, 200);
    const handleButtonClick = (event) => {
        const buttonRect = event.target.getBoundingClientRect();
        setButtonCoordinates({ x: buttonRect.x, y: buttonRect.bottom });
        toggleModal(); // Модальное окно открывается только при нажатии на кнопку
      };

  return (
    <>
      <Button variant='none' onClick={toggleModal}>  <img src={filter} width={10} height={10}/></Button>
      <div style={{ position: 'relative', display: 'inline-block' }}>
      {isOpen && (
          <div style={{ position: 'absolute', top: buttonCoordinates.y, left: buttonCoordinates.x }}>
            <Modal show onHide={toggleModal} centered backdrop={false} style={{ maxWidth: '200px' }}>
  <Modal.Header closeButton>
    <Modal.Title>Filter Options</Modal.Title>
  </Modal.Header>
  <Modal.Body>
    <Form.Control as="select" value={filterValue} onChange={e => onChange(e.target.value)}>
      <option value="">All</option>
      {options.map((option, index) => (
        <option key={index} value={option}>
          {option}
        </option>
      ))}
    </Form.Control>
  </Modal.Body>
  <Modal.Footer>
    <Button variant="secondary" onClick={toggleModal}>Close</Button>
    <Button variant="primary" onClick={() => applyFilter('')}>Clear</Button>
  </Modal.Footer>
</Modal>
</div>
        )}
      </div>
    </>
  );
};

export default Filter;
