         Filter: Filter,
      },
      {
        Header: 'номер зачетки',
        accessor: (row) =>  `${row.student.record_book_number}`,
         Filter: Filter,
      },
      {
        Header: 'Телефон',
        accessor: (row) =>  `${row.student.phone}`,
        Filter: Filter,
