      },
      {
        Header: 'год окончания предыдущ образования',
        accessor: (row) =>  `${row.student.year_of_prev_education_completion}`,
        Filter: Filter,
      },
      {
        Header: 'форма предыдущего образования',
        accessor: (row) =>  `${row.student.form_of_prev_education}`,
        Filter: Filter,
      },
      {
        Header: 'год поступления',
        accessor: (row) =>  `${row.student.year_of_admission}`,
        Filter: Filter,
      },
      {
        Header: 'особые отметки',
        accessor: (row) =>  `${row.student.special_marks}`,
        Filter: Filter,
      },
      
      {
        Header: 'приказы',
        columns: [
          {
            Header: 'допуск',
            accessor: (row) => `${row.workProtecton.final_grade}`,
            Filter: Filter,
            isVisible: true
          },
          {
            Header: 'тема',
            accessor: (row) => `${row.workProtecton.final_grade}`,
            Filter: Filter,
            isVisible: true
          },
          {
            Header: 'изменение темы',
            accessor: (row) => `${row.workProtecton.final_grade}`,
            Filter: Filter,
            isVisible: true
          },
          {
            Header: 'рецензент',
            accessor: (row) => `${row.workProtecton.final_grade}`,
            Filter: Filter,
            isVisible: true
          },
          {
            Header: 'отчисление',
            accessor: (row) => `${row.workProtecton.final_grade}`,
            Filter: Filter,
            isVisible: true
          },
          {
            Header: 'с какого числа отчисление',
            accessor: (row) => `${row.workProtecton.final_grade}`,
            Filter: Filter,
            isVisible: true
          }
        ]
      },
      {
        Header: 'отметки о выполнении',
        columns: [
          
      {
        Header: 'долги',
        accessor: (row) =>  `${row.student.number_of_debts}`,
        Filter: Filter
      },
      {
        Header: 'справка о выполнении уч. плана',
        accessor: (row) =>  `${row.student.certificate_of_curriculum_completion}`,
        Filter: Filter
      },
      {
        Header: 'сдана зачетка',
        accessor: (row) =>  `${row.student.record_book_submitted}`,
        Filter: Filter
      },
      {
        Header: 'сдана пояснительная записка',
        accessor: (row) => (row.explanatory_note_submitted ? 'да' : 'нет'),
        Filter: Filter
      },
      {
        Header: 'красный диплом',
        accessor: (row) => (row.student.diploma_with_honors ? 'да' : 'нет'),
        Filter: Filter
      },
      {
        Header: 'согласие на публикацию',
        accessor: (row) => (row.publication_agree ? 'да' : 'нет'),
        Filter: Filter
      },
      {
        Header: 'отзыв',
        accessor: (row) =>  `${row.teacher_review}`,
        Filter: Filter
      },
      {
        Header: 'рецензия',
        accessor: (row) =>  `${row.review}`,
        Filter: Filter
      },
      {
        Header: 'Акт о внедрении',
        accessor: (row) => (row.implementation_act ? 'да' : 'нет'),
        Filter: Filter
      },
      {
        Header: 'отчет о плагиате',
        accessor: (row) => (row.plagarism_report ? row.plagarism_report : 'нет'),
        Filter: Filter
      },
      {
        Header: 'заявление на последипломный отпуск',
        accessor: (row) => (row.student.application_for_postgraduate_leave ? row.student.application_for_postgraduate_leave: 'нет'),
        Filter: Filter
      },
      {
        Header: 'рекомендован к поступлению',
        accessor: (row) =>  `${row.student.recommended_for_admission}`,
        Filter: Filter
      },
        ]
      },
      {
        Header: 'вид ВКР',
        accessor: (row) =>  `${row.type_of_fqw}`,
        Filter: Filter,
        headerProps: { rowSpan: 2 },
      },
      {
        Header: 'ВКР по заявке',
        accessor: (row) =>  `${row.theme.fqw_by_application}`,
        Filter: Filter,
      },
      {
        Header: 'ВКР на англ. языке',
        accessor: (row) => (row.fqw_in_english ? 'да' : 'нет'),
        Filter: Filter,
      },
      {
        Header: 'ВКР рекомендовано',
        accessor: (row) =>  `${row.fqw_recommended}`,
        Filter: Filter,
      },
      {
        Header: 'наличие спец. условий',
        accessor: (row) => (row.special_conditions ? 'да' : 'нет'),
        Filter: Filter,
      },
    ],
    []
  );

  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow,
    allColumns,
    toggleHideColumn,
  } = useTable(
    { columns, data: tableData },
    useFilters // Use the useFilters plugin hook
  );

  const handleDoubleClick = (rowIndex, columnId, cellValue) => {
    setEditableCell({ rowIndex, columnId });
    setEditedCellValues({
      ...editedCellValues,
      [`${rowIndex}-${columnId}`]: cellValue,
    });
  };

  const handleCellChange = (e, rowIndex, columnId) => {
    const { value } = e.target;
    setEditedCellValues({
      ...editedCellValues,
      [`${rowIndex}-${columnId}`]: value,
    });
  };

  const handleBlur = (rowIndex, columnId) => {
    const editedValue = editedCellValues[`${rowIndex}-${columnId}`];
    const updatedRow = {
      ...tableData[rowIndex],
      [columnId]: editedValue !== undefined ? editedValue : tableData[rowIndex][columnId],
    };
    const updatedData = [...tableData];
    updatedData[rowIndex] = updatedRow;
    setTableData(updatedData);
    setEditedCellValues({});
    setEditableCell(null);
  };

  const handleKeyDown = (e, rowIndex, columnId) => {
    if (e.key === 'Enter') {
      handleBlur(rowIndex, columnId);
    }
  };

  return (
    <div>
      <h4 className="mb-4 text-center">Таблица данных</h4>
      <BlockInfo>
        Вы можете фильтровать данные, используя кнопку-фильтр в нужной ячейке, 
        а также скрывать столбцы, используя кнопку-крестик. Готовые данные могут быть экспортированы в эксель.
        Для удобства использования были составлены готовые списки. Для того чтобы сменить список, необходимо нажать на "Сброс" и выбрать необходимый список.
      </BlockInfo>
      <Row className='mb-3 ml-5'>
        <Col md={3}>
          <Button variant='none' className={styles.export_button}>
            Export to Excel
          </Button>
        </Col>
        <Col md={3}>
          <Button variant='none' className={styles.reset_button}>
            Сброс
          </Button>
        </Col>
      </Row>
      <h5>Формирование списков</h5>
      <Row className='my-3 ml-5'>
        <Col>
          <Button className={styles.button1}>
            Внутренние данные
          </Button>
        </Col>
        <Col>
          <Button className={styles.button1}>
            Список на защиту
          </Button>
        </Col>
        <Col>
          <Button className={styles.button1}>
            Отчет председателя
          </Button>
        </Col>
        <Col>
          <Button className={styles.button1}>
            Контроль
          </Button>
        </Col>
        <Col>
          <Button className={styles.button1}>
            Приказы
          </Button>
        </Col>
      </Row>
      <table {...getTableProps()} id="dataTable" className={`${styles.huge_table} mb-3`}>
        <thead>
          {headerGroups.map(headerGroup => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map(column => (
                <th
                  {...column.getHeaderProps()}
                  style={{
                    display: hiddenColumns.includes(column.id) ? 'none' : '',
                    position: 'relative',
                    paddingRight: '25px',
                  }}
                >
                  {column.render('Header')}
                  {column.canFilter && (
                    <div
                      style={{
                        position: 'absolute',
                        right: '0',
                        bottom: '0',
                        marginRight: '-15px',
                        marginBottom: '-9px'
                      }}
                    >
                      <Filter column={column} />
                    </div>
                  )}
                  {column.canFilter && (
                    <div
                      style={{
                        position: 'absolute',
                        right: '0',
                        top: '0',
                        marginRight: '-15px',
                        marginTop: '-10px'
                      }}
                    >
                      <Button variant="none" onClick={() => toggleColumnVisibility(column.id)}>
                        <img src={hide_cell} width={25} height={25} alt="Скрыть" />
                      </Button>
                    </div>
                  )}
                </th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {rows.map((row, rowIndex) => {
            prepareRow(row);
            return (
              <tr {...row.getRowProps()}>
                {row.cells.map((cell) => (
                  <td
                    {...cell.getCellProps()}
                    className={`${styles.data_cell} ${hiddenColumns.includes(cell.column.id) ? styles.hidden : ''}`}
                    style={{ 
                      display: hiddenColumns.includes(cell.column.id) ? 'none' : '',
                      width: cell.column.width
                    }}
                    onDoubleClick={() => handleDoubleClick(rowIndex, cell.column.id, cell.value)}
                  >
                    {editableCell && editableCell.rowIndex === rowIndex && editableCell.columnId === cell.column.id ? (
                      <input
                        type="text"
                        value={
                          editedCellValues[`${rowIndex}-${cell.column.id}`] !== undefined
                            ? editedCellValues[`${rowIndex}-${cell.column.id}`]
                            : cell.value
                        }
                        onChange={(e) => handleCellChange(e, rowIndex, cell.column.id)}
                        onBlur={() => handleBlur(rowIndex, cell.column.id)}
                        onKeyDown={(e) => handleKeyDown(e, rowIndex, cell.column.id)}
                      />
                    ) : (
                      <div>{cell.render('Cell')}</div>
                    )}
                  </td>
                ))}
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

