import React from 'react';

function ViewProtectionScheduleTable({ protection_schedule }) {
  // Helper function to convert date format
  const formatDate = (dateString) => {
    const months = [
      'января', 'февраля', 'марта', 'апреля', 'мая', 'июня',
      'июля', 'августа', 'сентября', 'октября', 'ноября', 'декабря'
    ];
    const day = dateString.slice(0, 2);
    const month = months[parseInt(dateString.slice(2, 4), 10) - 1];
    const year = dateString.slice(4, 8);
    return `${day} ${month} ${year}`;
  };

  // Group the schedule by group name
  const groupedByGroupName = protection_schedule.reduce((acc, item) => {
    const groupName = item.group.name;
    if (!acc[groupName]) {
      acc[groupName] = [];
    }
    acc[groupName].push(item);
    return acc;
  }, {});

  return (
    <>
    <h4>График защит ВКР</h4>
      {Object.keys(groupedByGroupName).map((groupName, index) => (
        <table key={index} border="1" style={{ marginBottom: '20px', width: '100%' }}>
          <thead>
            <tr>
              <th>ПП</th>
              <th>Дата</th>
              <th>Время</th>
              <th>Аудитория</th>
              <th>Уч. группа</th>
            </tr>
          </thead>
          <tbody>
            {groupedByGroupName[groupName].map((item, idx) => (
              <tr key={item.id}>
                <td>{idx + 1}</td>
                <td>{formatDate(item.date)}</td>
                <td>9:00</td>
                <td>{item.audience_number}</td>
                <td>{groupName}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ))}
    </>
  );
}

export default ViewProtectionScheduleTable;
