import React from 'react';
import { Table } from 'react-bootstrap';

function ViewProtectionSchedulePersons({ protection_schedule, students_list_for_schedule }) {
    // Функция для форматирования даты
    const formatDate = (dateString) => {
        const parts = dateString.match(/(\d{2})(\d{2})(\d{4})/);
        if (parts && parts.length === 4) {
            const year = parts[3];
            const month = parseInt(parts[2], 10);
            const day = parseInt(parts[1], 10);
            const months = [
                'января', 'февраля', 'марта', 'апреля', 'мая', 'июня',
                'июля', 'августа', 'сентября', 'октября', 'ноября', 'декабря'
            ];
            return `${day} ${months[month - 1]} ${year}`;
        } else {
            return 'Invalid Date';
        }
    };

    // Функция для получения ФИО студента
    const getFullName = (studentData) => {
        if (studentData && studentData.student) {
            const { last_name, first_name, patronymic } = studentData.student;
            return `${last_name} ${first_name} ${patronymic}`;
        } else {
            return 'Unknown';
        }
    };

    // Функция для получения группы по ID
    const getGroupNameById = (groupId) => {
        const group = protection_schedule.find(item => item.group?.id === groupId);
        return group ? group.group?.name : '';
    };

    // Группировка расписания по дням и группам
    const groupedSchedule = protection_schedule.reduce((acc, item) => {
        const dateKey = formatDate(item.date);
        if (!acc[dateKey]) {
            acc[dateKey] = {};
        }
        const groupName = getGroupNameById(item.group?.id);
        if (!acc[dateKey][groupName]) {
            acc[dateKey][groupName] = [];
        }
        const students = students_list_for_schedule.filter(student => student.student?.group?.id === item.group?.id);
        students.forEach(student => {
            const fullName = getFullName(student);
            acc[dateKey][groupName].push({ fullName, group: groupName, date: formatDate(item.date) });
        });
        return acc;
    }, {});

    // Функция для получения общего количества дней защиты для каждой группы
    const getNumberOfProtectionDays = (group) => {
        const groupSchedule = protection_schedule.filter(item => item.group?.id === group.id);
        return new Set(groupSchedule.map(item => item.date)).size;
    };

    // Функция для распределения студентов по дням защиты
    const distributeStudentsByProtectionDays = (schedule) => {
        Object.keys(schedule).forEach(date => {
            Object.keys(schedule[date]).forEach(group => {
                const students = schedule[date][group];
                const groupData = protection_schedule.find(item => item.group?.name === group);
                const numberOfDays = getNumberOfProtectionDays(groupData.group);
                if (students.length > 12) {
                    const studentsPerDay = Math.ceil(students.length / numberOfDays);
                    for (let i = 0; i < numberOfDays; i++) {
                        const startIndex = i * studentsPerDay;
                        const endIndex = startIndex + studentsPerDay;
                        const studentsForDay = students.slice(startIndex, endIndex);
                        const dayKey = getNextDay(date, i);
                        if (!schedule[dayKey]) {
                            schedule[dayKey] = {};
                        }
                        schedule[dayKey][group] = studentsForDay;
                    }
                } else {
                    if (!schedule[date]) {
                        schedule[date] = {};
                    }
                    schedule[date][group] = students;
                }
            });
        });
        return schedule;
    };

    // Функция для получения следующего дня
    const getNextDay = (date, daysToAdd) => {
        const parts = date.split(' ');
        const day = parseInt(parts[0], 10);
        const month = parts[1];
        const year = parts[2];
        const nextDate = new Date(`${year}-${getMonthNumber(month)}-${day + daysToAdd}`);
        return formatDate(nextDate.toLocaleDateString());
    };

    // Функция для получения номера месяца по названию
    const getMonthNumber = (monthName) => {
        const months = {
            'января': '01', 'февраля': '02', 'марта': '03', 'апреля': '04', 'мая': '05', 'июня': '06',
            'июля': '07', 'августа': '08', 'сентября': '09', 'октября': '10', 'ноября': '11', 'декабря': '12'
        };
        return months[monthName];
    };

    // Распределение студентов по дням защиты
    const distributedSchedule = distributeStudentsByProtectionDays(groupedSchedule);

    return (
        <div>
            {Object.entries(distributedSchedule).map(([date, groups]) => (
                <div key={date}>
                    <h3>{date}</h3>
                    <Table bordered hover>
                        <thead>
                            <tr>
                                <th>ФИО</th>
                                <th>Группа</th>
                                <th>Дата</th>
                            </tr>
                        </thead>
                        <tbody>
                            {Object.entries(groups).map(([group, students]) => (
                                students.map((student, index) => (
                                    <tr key={index}>
                                        <td>{student.fullName}</td>
                                        <td>{student.group}</td>
                                        <td>{student.date}</td>
                                    </tr>
                                ))
                            ))}
                        </tbody>
                        </Table>
                </div>
            ))}
        </div>
    );
}

export default ViewProtectionSchedulePersons;
