import React from 'react';
import { Table } from 'react-bootstrap';

const ViewProtectionSchedulePersons = ({ protection_schedule, students_list_for_schedule }) => {
    const formatDate = (date) => {
        const options = { day: 'numeric', month: 'long', year: 'numeric' };
        return date.toLocaleDateString('ru-RU', options);
    };

    const getFullName = (studentData) => {
        if (studentData && studentData.student) {
            const { last_name, first_name, patronymic } = studentData.student;
            return `${last_name} ${first_name} ${patronymic}`;
        } else {
            return 'Unknown';
        }
    };

    const distributeStudentsByProtectionDays = (schedule) => {
        const groupedSchedule = {};
        const addedStudents = [];

        schedule.forEach((item, index) => {
            if (!item.group) return;
            const dateKey = formatDate(item.date);
            const groupName = item.group ? item.group.name : '';
            const groupId = item.group ? item.group.id : '';

            if (!groupedSchedule[dateKey]) {
                groupedSchedule[dateKey] = {};
            }
            if (!groupedSchedule[dateKey][groupName]) {
                groupedSchedule[dateKey][groupName] = [];
            }

            const students = students_list_for_schedule.filter(student => student.student.group.id === groupId);
            students.forEach(student => {
                const fullName = getFullName(student);
                const existingStudent = addedStudents.find(s => s === fullName);
                if (!existingStudent) {
                    groupedSchedule[dateKey][groupName].push({ fullName, group: groupName, date: dateKey });
                    addedStudents.push(fullName);
                }
            });
        });

        return groupedSchedule;
    };

    const limitStudentsPerDay = (schedule) => {
        Object.entries(schedule).forEach(([date, groups]) => {
            Object.entries(groups).forEach(([group, students]) => {
                const totalStudents = students.reduce((acc, cur) => acc + cur.length, 0);
                if (totalStudents > 12) {
                    const firstDayStudents = [];
                    const secondDayStudents = [];
                    let count = 0;
                    students.forEach(student => {
                        if (count < 12) {
                            firstDayStudents.push(student);
                        } else {
                            secondDayStudents.push(student);
                        }
                        count += student.length;
                    });
    
                    schedule[date][group] = firstDayStudents;
                    const nextDay = new Date(date);
                    nextDay.setDate(nextDay.getDate() + 1);
                    const nextDayFormatted = formatDate(nextDay);
                    if (!schedule[nextDayFormatted]) {
                        schedule[nextDayFormatted] = {};
                    }
                    schedule[nextDayFormatted][group] = secondDayStudents;
                }
            });
        });
        return schedule;
    };
    

    const groupedSchedule = distributeStudentsByProtectionDays(protection_schedule);
    const limitedSchedule = limitStudentsPerDay(groupedSchedule);

    return (
        <div>
            {Object.entries(limitedSchedule).map(([date, groups]) => (
                <div key={date}>
                    <h3>{date}</h3>
                    {Object.entries(groups).map(([group, students]) => (
                        <Table bordered hover key={group}>
                            <thead>
                                <tr>
                                    <th>ФИО</th>
                                    <th>Группа</th>
                                    <th>Дата</th>
                                </tr>
                            </thead>
                            <tbody>
                                {students.map((student, index) => (
                                    <tr key={index}>
                                        <td>{student.fullName}</td>
                                        <td>{student.group}</td>
                                        <td>{student.date}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </Table>
                    ))}
                </div>
            ))}
        </div>
    );
};

export default ViewProtectionSchedulePersons;
