import React, { useState } from 'react';
import styles from './Background.module.css';

const Background = ({ children, darkMode, toggleTheme }) => {
  return (
    <div className={darkMode ? `${styles.body} ${styles['dark-mode']}` : styles.body}>
      {children}
      <button onClick={toggleTheme}>Toggle Theme</button> {/* Просто для тестирования */}
    </div>
  );
};

export default Background;
