import petrovich from 'petrovich';
import RussianNouns from 'russian-nouns-js';
import { saveAs } from 'file-saver';
import PizZip from 'pizzip';
import Docxtemplater from 'docxtemplater';
import word from './1.docx';


const generateDocument = ({studentData, teacherData, requestData}) => {
  console.log (studentData)
    const fullname = studentData.surname + ' ' + studentData.name + ' ' + studentData.patronymic

    const np_s = studentData.name.charAt(0) + '.'+ studentData.patronymic.charAt(0) + '. ';
    const np_t = teacherData.name.charAt(0) + '.' + teacherData.patronymic.charAt(0) + '. ';

    var surname_gen = petrovich.female.last.genitive (studentData.surname);
    var teacher = {
    last: teacherData.surname,
    first: teacherData.name,
    middle: teacherData.patronymic
    };

    var fullnameTeac = petrovich(teacher, 'accusative');
    fullnameTeac = fullnameTeac.last + ' ' + fullnameTeac.first + ' '+ fullnameTeac.middle

    const rne = new RussianNouns.Engine();
    const form_of_studying = studentData.form_of_studying;
    const academic_title = teacherData.academic_title;
    const academic_degree = teacherData.academic_degree;
    const job_title = teacherData.job_title;
    const place_of_work = teacherData.place_of_work;

    const replaceFirstWordWithDeclension = (text, gender) => {
      const firstWord = text.split(" ")[0];
      const declinedWord = rne.decline({ text: firstWord, gender: gender }, 'родительный');
      return text.replace(firstWord, declinedWord);
    };

    const declinedForm = replaceFirstWordWithDeclension(form_of_studying, 'женский');
    const declinedTitle = replaceFirstWordWithDeclension(academic_title, 'средний');
    const declinedDegree = replaceFirstWordWithDeclension(academic_degree, 'средний');
    const declinedJobTitle = replaceFirstWordWithDeclension(job_title, 'средний');
    const declinedPlace = replaceFirstWordWithDeclension(place_of_work, 'женский');

    

    const xhr = new XMLHttpRequest();
    xhr.open('GET', word, true);
    xhr.responseType = 'arraybuffer';


    xhr.onload = () => {
      if (xhr.status === 200) {
        const data = new Uint8Array(xhr.response);
        const zip = new PizZip(data);
        let doc;
        try {
          doc = new Docxtemplater(zip);
        } catch (error) {
          console.error('Ошибка создания документа:', error);
          return;
        }


        const dataToRender = {
          np_s: np_s,
          np_t: np_t,
          surname: studentData.surname,
          surname_gen: surname_gen,
          phoneNumber: studentData.phone_number,
          email: studentData.email,
          kurs: studentData.kurs,
          group: studentData.group,
          specialtyCode: studentData.specialtyCode,
          specialtyName: studentData.specialtyName,
          fieldOfStudyCode: studentData.fieldOfStudyCode,
          fieldOfStudyName: studentData.fieldOfStudyName,
          form_of_studying: form_of_studying,
          base_of_studying: studentData.basis_of_study,
          fullname: fullname,
          theme_str: requestData.theme_str,
          fullnameTeacher:fullnameTeac,
          surnameTeacher: teacherData.surname,
          academic_degree: academic_degree,
          academic_title: academic_title,
          job_title: job_title,
          place_of_work: place_of_work,
        };

        doc.setData(dataToRender);

        try {
          doc.render();
        } catch (error) {
          console.error('Ошибка рендеринга документа:', error);
          return;
        }

        const output = doc.getZip().generate({ type: 'blob' });
        saveAs(output, 'generated_document.docx');
      } else {
        console.error('Ошибка загрузки файла:', xhr.statusText);
      }
    };

    xhr.onerror = () => {
      console.error('Ошибка загрузки файла:', xhr.statusText);
    };

    xhr.send();
  };

export default generateDocument

function extractVariables(template) {
    var re = /({.*?})/sg;
    let variables = [];
    let result;
    while ((result = re.exec(template)) !== null) {
        variables.push(result[0]); 
    }
    return variables;
}
