import React from 'react';
import { Table } from 'react-bootstrap';

const ViewProtectionSchedulePersons = ({ protection_schedule, students_list_for_schedule }) => {
    const formatDate = (dateString) => {
        const parts = dateString.match(/(\d{2})(\d{2})(\d{4})/);
        if (parts && parts.length === 4) {
            const year = parts[3];
            const month = parseInt(parts[2], 10);
            const day = parseInt(parts[1], 10);
            const months = [
                'января', 'февраля', 'марта', 'апреля', 'мая', 'июня',
                'июля', 'августа', 'сентября', 'октября', 'ноября', 'декабря'
            ];
            return `${day} ${months[month - 1]} ${year}`;
        } else {
            return 'Invalid Date';
        }
    };

    const getFullName = (studentData) => {
        if (studentData && studentData.student) {
            const { last_name, first_name, patronymic } = studentData.student;
            return `${last_name} ${first_name} ${patronymic}`;
        } else {
            return 'Unknown';
        }
    };

    const distributeStudentsByProtectionDays = (schedule) => {
        const groupedSchedule = schedule.reduce((acc, item) => {
            const dateKey = formatDate(item.date);
            if (!acc[dateKey]) {
                acc[dateKey] = {};
            }
            const groupName = item.group ? item.group.name : '';
            if (!acc[dateKey][groupName]) {
                acc[dateKey][groupName] = [];
            }
            const students = students_list_for_schedule.filter(student => student.student.group.id === item.group.id);
            students.forEach(student => {
                const fullName = getFullName(student);
                acc[dateKey][groupName].push({ fullName, group: groupName, date: formatDate(item.date) });
            });
            return acc;
        }, {});

        Object.keys(groupedSchedule).forEach(date => {
            Object.keys(groupedSchedule[date]).forEach(group => {
                const students = groupedSchedule[date][group];
                const protectionDays = protection_schedule.find(item => item.group.name === group)?.group?.protection_days || 1;
                const studentsPerDay = Math.ceil(students.length / protectionDays);
                let remainingStudents = students.slice();
                for (let i = 0; i < protectionDays; i++) {
                    const currentStudents = remainingStudents.slice(0, studentsPerDay);
                    if (!groupedSchedule[getNextDay(date, i)]) {
                        groupedSchedule[getNextDay(date, i)] = {};
                    }
                    groupedSchedule[getNextDay(date, i)][group] = currentStudents;
                    remainingStudents = remainingStudents.slice(studentsPerDay);
                }
                delete groupedSchedule[date][group];
            });
        });

        return groupedSchedule;
    };

    const getNextDay = (dateString, daysToAdd) => {
        const date = new Date(dateString);
        date.setDate(date.getDate() + daysToAdd);
        return formatDate(date.toLocaleDateString());
    };

    return (
        <div>
            {Object.entries(distributeStudentsByProtectionDays(protection_schedule)).map(([date, groups]) => (
                <div key={date}>
                    <h3>{date}</h3>
                    <Table bordered hover>
                        <thead>
                            <tr>
                                <th>ФИО</th>
                                <th>Группа</th>
                                <th>Дата</th>
                            </tr>
                        </thead>
                        <tbody>
                            {Object.entries(groups).map(([group, students]) => (
                                students.map((student, index) => (
                                    <tr key={index}>
                                        <td>{student.fullName}</td>
                                        <td>{student.group}</td>
                                        <td>{student.date}</td>
                                    </tr>
                                ))
                            ))}
                        </tbody>
                    </Table>
                </div>
            ))}
        </div>
    );
};

export default ViewProtectionSchedulePersons;
