import React, { Component, useState } from 'react'
import styles from "./GenerateApplicationForm.module.css"
import styles_base from "../../Forms/FormsBase.module.css"
import BlockInfo from '../../BlockInfo/BlockInfo'
import { Form, Button, Col, Row } from 'react-bootstrap';

const направления = {
    "09.03.02-Информационные системы и технологии": "09.03.02.30-Информационные системы и технологии",
    "09.04.01-Информатика и вычислительная техника": "09.04.01.10-Интеллектуальные информационные системы",
    "09.04.02-Информационные системы и технологии": [
      "09.04.02.01-Информационно-управляющие системы",
      "09.04.02.05-Информационные системы дистанционного зондирования Земли"
    ]
  };

export default class GenerateApplicationForm extends Component {
    constructor(props) {
        super(props);
        this.state = {
          showFields: false
        };
      }
    
      handleRadioChange = (e) => {
        this.setState({ showFields: e.target.value === 'option1' });
      }
      
  render() {
    const [selectedKey, setSelectedKey] = useState('');
    const [selectedValue, setSelectedValue] = useState('');

    const handleKeyChange = (event) => {
    const key = event.target.value;
    setSelectedKey(key);
    const value = направления[key];
    setSelectedValue(Array.isArray(value) ? '' : value);
    };

    const handleValueChange = (event) => {
    setSelectedValue(event.target.value);
    };
    return (
        <div className={styles.container}>
      <div className={styles.card}>
      <h4 class="mb-2 text-center">Данные для заявления на ВКР</h4>
      <BlockInfo>
        Проверьте данные в форме, при необходимости внесите правки и сформируйте заявление на утверждение темы ВКР
        </BlockInfo>
        <Form className="px-4 py-2 m-2">
        <h5 className="text-center mb-2">Данные выпускника</h5>
        <div className="block_form">
          <Form.Group as={Row}>
            <Form.Label column sm={4}>ФИО</Form.Label>
            <Col sm={8}>
              <Form.Control type="text" className={styles_base.form_control} placeholder="ФИО" />
            </Col>
          </Form.Group>
          <Form.Group as={Row}>
        <Form.Label column sm={4}>Контактный телефон</Form.Label>
        <Col sm={8}>
            <Form.Control type="text" className={styles_base.form_control} placeholder="номер телефона" />
        </Col>
        </Form.Group>
        <Form.Group as={Row}>
      <Form.Label column sm={4}>Email</Form.Label>
      <Col sm={8}>
        <Form.Control type="text"  className={styles_base.form_control} placeholder="email" />
      </Col>
    </Form.Group>
    <Form.Group as={Row}>
      <Form.Label column sm={4}>Курс</Form.Label>
      <Col sm={8}>
        <Form.Control type="text" className={styles_base.form_control} placeholder="4" />
      </Col>
    </Form.Group>
    <Form.Group as={Row}>
      <Form.Label column sm={4}>Группа</Form.Label>
      <Col sm={8}>
        <Form.Control type="text" className={styles_base.form_control} placeholder="группа" />
      </Col>
    </Form.Group>
    <Form.Group as={Row}>
        <Form.Label column sm={4}>Направление подготовки/специальности:</Form.Label>
        <Col sm={8}>
          <Form.Control as="select" value={selectedKey} onChange={handleKeyChange}>
            <option value="">Направление подготовки/специальности</option>
            {Object.keys(направления).map((key) => (
              <option key={key} value={key}>{key}</option>
            ))}
          </Form.Control>
        </Col>
      </Form.Group>

      <Form.Group as={Row}>
        <Form.Label column sm={4}>Профиль/специализация:</Form.Label>
        <Col sm={8}>
          <Form.Control as="select" value={selectedValue} onChange={handleValueChange}>
            <option value="">Профиль/специализация</option>
            {Array.isArray(направления[selectedKey]) && направления[selectedKey].map((value) => (
              <option key={value} value={value}>{value}</option>
            ))}
          </Form.Control>
        </Col>
      </Form.Group>
        </div>

        <h5 className="my-4 text-center">Данные консультанта</h5>
        <div className="block_form">
          <Form.Group as={Row}>
            <Form.Label column sm={5}>Есть консультант</Form.Label>
            <Col sm={7}>
              <Form.Check
                inline
                type="radio"
                label="есть"
                value="option1"
                name="options"
                onChange={this.handleRadioChange}
              />
              <Form.Check
                inline
                type="radio"
                label="нет"
                value="option2"
                name="options"
                onChange={this.handleRadioChange}
              />
            </Col>
          </Form.Group>
          {this.state.showFields && (
            <div id="fieldsToShow">
              <Form.Group as={Row}>
                {/* Поля для консультанта */}
              </Form.Group>
            </div>
          )}
        </div>
        <Button type="submit" className="button-form">Отправить</Button>
      </Form>


        </div>
        </div>
    )
  }
}
