import React, { useState } from 'react';
import { Table } from 'react-bootstrap';

function ThemesListReading({ themes_list }) {
  const [filter, setFilter] = useState('');
  const [selectedTeachers, setSelectedTeachers] = useState([]);

  const filteredThemes = themes_list.filter((theme) => {
    if (!filter && selectedTeachers.length === 0) {
      return true; // Если фильтры не указаны, показываем все темы
    }
    // Проверяем, содержит ли ФИО преподавателя или строка "кафедра"
    return (
      (theme.teacher.user.id !== null &&
        `${theme.teacher.user.last_name} ${theme.teacher.user.first_name} ${theme.teacher.user.patronymic}`
          .toLowerCase()
          .includes(filter.toLowerCase())) ||
      'кафедра сии'.toLowerCase().includes(filter.toLowerCase())
    );
  });

  const handleTeacherCheckboxChange = (event) => {
    const teacherName = event.target.value;
    if (event.target.checked) {
      setSelectedTeachers([...selectedTeachers, teacherName]);
    } else {
      setSelectedTeachers(selectedTeachers.filter((name) => name !== teacherName));
    }
  };

  const teacherOptions = [...new Set(themes_list.map((theme) => theme.teacher.user))].filter(
    (user) => user.id !== null
  );

  return (
    <div>
      <h4 className='text-center'>Список тем</h4>
      <div className='mx-5 mt-4'>
        <input
          type='text'
          className='form-control mb-3'
          placeholder='Фильтр по преподавателю или кафедре'
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
        />
        <div>
          {teacherOptions.map((user) => (
            <label key={user.id} style={{ marginRight: '10px' }}>
              <input
                type='checkbox'
                value={`${user.last_name} ${user.first_name} ${user.patronymic}`}
                checked={selectedTeachers.includes(`${user.last_name} ${user.first_name} ${user.patronymic}`)}
                onChange={handleTeacherCheckboxChange}
              />{' '}
              {`${user.last_name} ${user.first_name} ${user.patronymic}`}
            </label>
          ))}
        </div>
        <Table bordered hover>
          <thead>
            <tr>
              <th>ID</th>
              <th>Название</th>
              <th>Преподаватель</th>
              <th>Организация</th>
              <th>Компания</th>
            </tr>
          </thead>
          <tbody>
            {filteredThemes.map((theme) => (
              <tr key={theme.id}>
                <td>{theme.id}</td>
                <td>{theme.name}</td>
                <td>
                  {theme.teacher.user.id !== null ? (
                    `${theme.teacher.user.last_name} ${theme.teacher.user.first_name} ${theme.teacher.user.patronymic}`
                  ) : (
                    'кафедра сии'
                  )}
                </td>
                <td>{theme.department ? 'Да' : 'Нет'}</td>
                <td>
                  {theme.company.name && (
                    <>
                      <div>Название: {theme.company.name}</div>
                      Ответсвенный:
                      <div>
                        ФИО: {theme.company.last_name_of_responsible} {theme.company.first_name_of_responsible}{' '}
                        {theme.company.patronymic_of_responsible}
                      </div>
                      <div>Должность: {theme.company.job_title_of_resposible}</div>
                      <div>Дополнительная информация: {theme.company.additional_information}</div>
                    </>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      </div>
    </div>
  );
}

export default ThemesListReading;
