import React from 'react';
import BaseModal from '../BaseModal/BaseModal';
import { Button, Row, Col } from 'react-bootstrap';
import styles_base from '../BaseModal/BaseModal.module.css';
import { Link } from 'react-router-dom';

const ThemeListModal = ({ show, handleClose, darkMode, tableData, name, type_of_user }) => {
  const themesToShow = tableData.map(row => row.theme);

  let roleText;
  switch (type_of_user) {
    case 'руководитель':
      roleText = 'Руководитель: ';
      break;
    case 'секретарь ГЭК':
      roleText = 'Кафедра СИИ ';
      break;
    default:
      roleText = 'Неизвестный тип пользователя: ';
  }

  return (
    <BaseModal
      show={show}
      modalClass={styles.container_modal}
      handleClose={handleClose}
      title={'Список тем'}
      type_of_user={type_of_user}
      darkMode={darkMode}
      sourceData={tableData}
      footer={
        <Row className="justify-content-between px-3">
          <Col md={5}>
            <Button variant='none' className={styles_base.button_cancel} onClick={handleClose}>Отмена</Button>
          </Col>
          <Col md={5}>
            <Link to={{ pathname: "/edit_theme_list_teacher", state: { tableData: tableData } }}>
              <Button variant='none' className={styles_base.button_next} onClick={handleClose}>Редактировать</Button>
            </Link>
          </Col>
        </Row>
      }
    >
      <p className='fw-bold'> {roleText} {name} </p>
      {themesToShow.map((theme, index) => (
        <p key={index} className='my-n3'>{theme}</p>
      ))}
    </BaseModal>
  );
};

export default ThemeListModal;
