import React, { useEffect, useState } from 'react';
import BaseModal from '../BaseModal/BaseModal';
import { Row, Col, Button } from 'react-bootstrap';
import styles_base from '../BaseModal/BaseModal.module.css';
import ScheduleFQWModal from '../ScheduleFQWModal/ScheduleFQWModal';
import { Link } from 'react-router-dom';
import axios from 'axios';

function ModalViewGraphExists({ show, handleClose, darkMode, teacherData, requestId }) {
  const [showScheduleModal, setShowScheduleModal] = useState(false);
  const [scheduleStudProf, setScheduleStudProf] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        if (requestId) {
          const token = localStorage.getItem('token');
          const response = await axios.get(`http://127.0.0.1:8000/v1/main/teacher/work_schedule_by_request/`, {
            headers: {
              Authorization: `Token ${token}`,
            },
            body: {
         
                id: `Token ${requestId}`,
 
            }
          });
          setScheduleStudProf(response.data);
        }
      } catch (error) {
        console.error('Error fetching schedule:', error);
      }
    };

    fetchData();
  }, [requestId]); // Передаем requestId как зависимость, чтобы useEffect срабатывал при его изменении

  const handleScheduleModalOpen = () => {
    setShowScheduleModal(true);
  };

  const handleScheduleModalClose = () => {
    setShowScheduleModal(false);
  };

  const content =
    scheduleStudProf.length > 0 ? 'График был создан' : 'График работы над ВКР еще не был создан';

  const footer = scheduleStudProf.length > 0 ? (
    <Row className="justify-content-between px-3">
      <Col md={5}>
        <Button
          variant="none"
          onClick={handleScheduleModalOpen}
          className={styles_base.button_cancel}
        >
          Просмотреть
        </Button>
      </Col>
      <Col md={5}>
        <Button variant="none" className={styles_base.button_next} onClick={handleClose}>
          Отмена
        </Button>
      </Col>
    </Row>
  ) : (
    <Row className="justify-content-between px-3">
      <Col>
        <Link to="/create_schedule_work_fqw">
          <Button variant="none" className={styles_base.button_next} onClick={handleClose}>
            Создать
          </Button>
        </Link>
      </Col>
      <Col md={5}>
        <Button variant="none" className={styles_base.button_next} onClick={handleClose}>
          ОК
        </Button>
      </Col>
    </Row>
  );

  return (
    <>
      <BaseModal
        show={show}
        handleClose={handleClose}
        title={'График работы над ВКР'}
        content={content}
        darkMode={darkMode}
        sourceData={scheduleStudProf}
        footer={footer}
        data={teacherData}
      />
      <ScheduleFQWModal
        show={showScheduleModal}
        handleClose={handleScheduleModalClose}
        darkMode={false}
        schedule_stud_prof={scheduleStudProf}
        data={teacherData}
      />
    </>
  );
}

export default ModalViewGraphExists;
