import React, { useState, useEffect } from 'react';
import { Button, Form, Col, Row } from 'react-bootstrap';
import styles from "./ChooseTheme.module.css"
import styles_base from "../../Forms/FormsBase.module.css"
import { useLocation } from 'react-router-dom';

const ChooseTheme = ({ themes_list }) => {
  const [selectedOption, setSelectedOption] = useState('');
  const [url, setUrl] = useState('');
  const location = useLocation();

  useEffect(() => {
    const teacherName = decodeURIComponent(location.pathname.substring(location.pathname.lastIndexOf('/') + 1));
    setUrl(teacherName);
  }, [location]);

  const handleSelectChange = (option) => {
    setSelectedOption(option);
  };

  const filteredDepartmentThemes = themes_list.filter(theme => theme.department);
  const filteredTeacherThemes = themes_list.filter(theme => {
    const teacher = theme.teacher;
    return `${teacher.user.last_name} ${teacher.user.first_name} ${teacher.user.patronymic}` === url;
  });

  const studentTheme = themes_list.find(theme => theme.by_student);

  return (
    <div>
      {/* Ваш JSX компонента */}
    </div>
  );
};

export default ChooseTheme;
