import React, { useState } from 'react';
import { Container, Card, Row, Col, Form, Button, Tooltip } from 'react-bootstrap';
import prev from '../../../img/prev.png'; 
import next from '../../../img/next.png';
import styles from '../FormsBase.module.css';

const MarksCompletionForm = ({studentData, generating_info}) => {
  const [currentStep, setCurrentStep] = useState(0);

  const nextStep = () => {
    setCurrentStep(currentStep + 1);
  };

  const prevStep = () => {
    setCurrentStep(currentStep - 1);
  };
  const [numberOfDebts, setNumberOfDebts] = useState(studentData.number_of_debts || '');
  const [certificateDate, setCertificateDate] = useState(studentData.certificate_of_curriculum_completion || '');
  const [recordBookSubmitted, setRecordBookSubmitted] = useState('');
  const [diplomaWithHonors, setDiplomaWithHonors] = useState('');
  const [publicationAgree, setPublicationAgree] = useState(generating_info.publication_agree || '');
  const [teacherReview, setTeacherReview] = useState(generating_info.teacher_review || '0');
  const [review, setReview] = useState(generating_info.review || '0');
  const [implementationAct, setImplementationAct] = useState(generating_info.implementation_act || 'нет');
  const [plagarismReport, setPlagarismReport] = useState(generating_info.plagarism_report || 0);
  const [applicationForVacation, setApplicationForVacation] = useState(studentData.application_for_postgraduate_leave || 'нет');
  const [recommendedForAdmission, setRecommendedForAdmission] = useState(studentData.recommended_for_admission || 'нет');

  const handleImplementationActChange = (event) => {
    setImplementationAct(event.target.value);
  };

  const handlePlagarismReportChange = (event) => {
    setPlagarismReport(event.target.value);
  };

  const handleApplicationForVacationChange = (event) => {
    setApplicationForVacation(event.target.value);
  };

  const handleRecommendedForAdmissionChange = (event) => {
    setRecommendedForAdmission(event.target.value);
  };
  const handleTeacherReviewChange = (event) => {
    setTeacherReview(event.target.value);
  };

  const handleReviewChange = (event) => {
    setReview(event.target.value);
  };
  const handleRecordBookChange = (event) => {
    setRecordBookSubmitted(event.target.value);
};
const handleDiplomaChange = (event) => {
    setDiplomaWithHonors(event.target.value);
};
const handlePublicationAgreeChange = (event) => {
  setPublicationAgree(event.target.value);
};


  const handleNumberOfDebtsChange = (event) => {
    setNumberOfDebts(event.target.value);
  };

  const handleCertificateDateChange = (event) => {
    setCertificateDate(event.target.value);
  };
  return (

      <Row >
        <Col md={8} className='px-5'>
          <h4 className="mb-4 text-center">Форма</h4>
          {currentStep === 0 && (
            <div className={`step ${currentStep === 0 ? 'active' : ''}`}>
              <Form.Group as={Row}>
                <Form.Label column sm={5} >
                 Долги  
                </Form.Label>
                <Col sm={7}>
                  <Form.Control className={styles.form_control} type="text" defaultValue={studentData.number_of_debts} placeholder="количество долгов"  onChange={handleNumberOfDebtsChange}/>
                </Col>
              </Form.Group>

              <Form.Group as={Row} data-toggle="tooltip" title="Если справка сдана, укажите дату. Если не сдана, оставьте поле пустым">
                <Form.Label column sm={5}>Справка о выполнении уч.плана</Form.Label>
                <Col sm={7}>
                  <Form.Control className={styles.form_control} type="date" defaultValue={studentData.certificate_of_curriculum_completion} placeholder="дата справки"  onChange={handleCertificateDateChange}/>
                </Col>
              </Form.Group>

              <Form.Group as={Row}>
  <Form.Label column sm={5}>Сдана зачетка</Form.Label>
  <Col sm={5} className="d-flex align-items-center">
    <Form.Check
      inline
      type="radio"
      name="recordBook"
      id="gradebook-yes"
      label="да"
      value="да"
      defaultChecked={studentData.record_book_submitted === true}
    />
    <Form.Check
      inline
      type="radio"
      name="recordBook"
      id="gradebook-no"
      label="нет"
      value="нет"
      defaultChecked={studentData.record_book_submitted === false}
    />
  </Col>
</Form.Group>

<Form.Group as={Row}>
  <Form.Label column sm={5}>Красный диплом</Form.Label>
  <Col sm={7} className="d-flex align-items-center">
    <Form.Check
      inline
      type="radio"
      name="diploma"
      id="prestigious_diploma-yes"
      label="да"
      value="да"
      defaultChecked={studentData.diploma_with_honors === true}
    />
    <Form.Check
      inline
      type="radio"
      name="diploma"
      id="prestigious_diploma-no"
      label="нет"
      value="нет"
      defaultChecked={studentData.diploma_with_honors === false}
    />
  </Col>
</Form.Group>



<Form.Group as={Row}>
      <Form.Label column sm={5}>Согласие на публикацию</Form.Label>
      <Col sm={7} className="d-flex align-items-center">
        <Form.Check
          inline
          type="radio"
          name="consentToPublication"
          id="consent_to_publication-yes"
          label="да"
          value="да"
          checked={publicationAgree === true}
          onChange={handlePublicationAgreeChange}
        />
        <Form.Check
          inline
          type="radio"
          name="consentToPublication"
          id="consent_to_publication-no"
          label="нет"
          value="нет"
          checked={publicationAgree === false}
          onChange={handlePublicationAgreeChange}
        />
      </Col>
    </Form.Group>

    <Form.Group as={Row}>
        <Form.Label column sm={5}>Отзыв</Form.Label>
        <Col sm={7}>
          <Form.Control
            as="select"
            id="review_mark"
            className={styles.form_control}
            value={teacherReview}
            onChange={handleTeacherReviewChange}
          >
            <option value="0">-</option>
            <option value="2">неудовлетворительно</option>
            <option value="3">удовлетворительно</option>
            <option value="4">хорошо</option>
            <option value="5">отлично</option>
          </Form.Control>
        </Col>
      </Form.Group>

      <Form.Group as={Row}>
        <Form.Label column sm={5}>Рецензия</Form.Label>
        <Col sm={7}>
          <Form.Control
            as="select"
            id="critique"
            className={styles.form_control}
            value={review}
            onChange={handleReviewChange}
          >
            <option value="0">-</option>
            <option value="2">неудовлетворительно</option>
            <option value="3">удовлетворительно</option>
            <option value="4">хорошо</option>
            <option value="5">отлично</option>
          </Form.Control>
        </Col>
      </Form.Group>

      <Form.Group as={Row}>
        <Form.Label column sm={5}>Акт о внедрении</Form.Label>
        <Col sm={7} className="d-flex align-items-center">
          <Form.Check inline type="radio" name="act_of_implementation" id="act_of_implementation-yes" label="есть" />
          <Form.Check inline type="radio" name="act_of_implementation" id="act_of_implementation-no" label="нет" />
        </Col>
      </Form.Group>

      <Form.Group as={Row}>
        <Form.Label column sm={5}>Отчет о плагиате</Form.Label>
        <Col sm={7}>
          <Form.Control type="number" min="0" max="100" id="input9" placeholder="% оригинальности"  className={styles.form_control}/>
        </Col>
      </Form.Group>

      <Form.Group as={Row}>
        <Form.Label column sm={5}>Заявление на последипломный отпуск</Form.Label>
        <Col sm={7} className="d-flex align-items-center">
          <Form.Check inline type="radio" name="application_for_vacation" id="application_for_vacation-yes" label="есть" />
          <Form.Check inline type="radio" name="application_for_vacation" id="application_for_vacation-no" label="нет" />
        </Col>
      </Form.Group>

      <Form.Group as={Row}>
        <Form.Label column sm={5}>Рекомендован к поступлению:</Form.Label>
        <Col sm={7}>
          <Form.Control as="select" id="dropdown" className={styles.form_control}>
            <option value="нет">Нет</option>
            <option value="в магистратуру">В магистратуру</option>
            <option value="в аспирантуру">В аспирантуру</option>
          </Form.Control>
        </Col>
      </Form.Group>

              
              <Button variant="none" className={styles.next} onClick={nextStep}>
                <img src={next} width="10%" alt="Следующий" />
              </Button>
            </div>
          )}
          {currentStep === 1 && (
            <div className={`step ${currentStep === 1 ? 'active' : ''}`}>
              <Form.Group as={Row}>
        <Form.Label column sm={5}>Вид ВКР</Form.Label>
        <Col sm={7}>
          <Form.Control as="select" id="type_vkr" className={styles.form_control}>
            <option value="академическое">академическое</option>
            <option value="проектно-исследовательское">проектно-исследовательское</option>
            <option value="комплексное">комплексное</option>
            <option value="в формате стартапа">в формате стартапа</option>
            <option value="в формате стартапа с актом внедрения">в формате стартапа с актом внедрения</option>
          </Form.Control>
        </Col>
      </Form.Group>

      <Form.Group as={Row} data-toggle="tooltip" title="Если у Вас комплексная ВКР, укажите общую тему, в иных случаях укажите дополнительную информацию при необходимости либо оставьте поле пустым">
        <Form.Label column sm={5}>Дополнительно</Form.Label>
        <Col sm={7}>
          <Form.Control type="text" id="additional" placeholder="дополнительно" className={styles.form_control} />
        </Col>
      </Form.Group>

      <Form.Group as={Row}>
        <Form.Label column sm={5}>ВКР по заявке</Form.Label>
        <Col sm={7}>
          <Form.Control as="select" id="on_request" className={styles.form_control}>
            <option value="предприятия">предприятия</option>
            <option value="подразделения СФУ">подразделения СФУ</option>
            <option value="предложено студентом">предложено студентом</option>
          </Form.Control>
        </Col>
      </Form.Group>

      <Form.Group as={Row}>
        <Form.Label column sm={5}>ВКР на английском языке</Form.Label>
        <Col sm={7} className="d-flex align-items-center">
          <Form.Check inline type="radio" name="english" id="english-yes" label="да" />
          <Form.Check inline type="radio" name="english" id="english-no" label="нет" />
        </Col>
      </Form.Group>

      <Form.Group as={Row}>
        <Form.Label column sm={5}>ВКР рекомендовано</Form.Label>
        <Col sm={7}>
          <Form.Control as="select" id="is_recommended" className={styles.form_control}>
            <option value="к внедрению">к внедрению</option>
            <option value="имеется внедрение">имеется внедрение</option>
            <option value="к опубликованию">к опубликованию</option>
            <option value="имеется публикация">имеется публикация</option>
          </Form.Control>
        </Col>
      </Form.Group>

      <Form.Group as={Row}>
        <Form.Label column sm={5}>Наличие специальных условий</Form.Label>
        <Col sm={7} className="d-flex align-items-center">
          <Form.Check inline type="radio" name="special_conditions" id="special_conditions-yes" label="требуется" />
          <Form.Check inline type="radio" name="special_conditions" id="special_conditions-no" label="не требуется" />
        </Col>
      </Form.Group>


<Row>
  <Col>
              <Button variant="none" className={styles.prev} onClick={prevStep}>
                <img src={prev} width="18%" alt="Предыдущий" />
              </Button>
              </Col>
              <Col>
              <Button type="submit" variant="none" className={styles.button_form}>Отправить</Button>
              </Col>
              </Row>
            </div>
          )}
        </Col>
        <div className={styles.vertical_line} ></div>
        <Col md={3} className="mx-3">
              <h4 className="mb-4 text-center">Правила заполнения формы</h4>
              <p>
                Данная форма доступна для подачи данных о выполнении отметок образовательного процесса вплоть до того
                момента, как будет проверена секретарем ГЭК. Далее выпускник не сможет изменить данные об отметках.
                Заполняйте данную форму внимательно. Если сомневаетесь в правильности заполнения полей, оставьте их пустыми.
              </p>
        </Col>
      </Row>
  
  );
};

export default MarksCompletionForm;
