import React from 'react'
import styles from './SupervisedStudents.module.css'
import {Row, Col} from 'react-bootstrap'


function SupervisedStudentsCard({teacherData, requestData, work_schedule, work_protection}) {
    const protection = work_protection.find(wp => wp.id_request === requestData.id);
  return (
    <>
    <h4 className="mb-4 text-center">Курируемые выпускники</h4>
    <div className={styles.block}>

    {requestData.map((request) => {
          if (request.is_accepted_request === true) {
            return (
              <div key={request.id}>
                <Row className='justify-content-center'>
                    <Col>{request.student.surname} {request.student.name} {request.student.patronymic} </Col>
                    <Col>{request.student.group} </Col>
                </Row>
                <Row className='justify-content-center'>
                    <Col>Тема: </Col>
                    <Col>{request.theme_str} </Col>
                </Row>

                <Row className='justify-content-center'>
                <Col>Этап работы: </Col>
                if (work_schedule.id_request == key) {
                <Col>график не был создан </Col>
                }
                     <Col>{work_schedule.stage_name} </Col>
                  
                </Row>

                <Row className='justify-content-center'>
                <Col>Дата и время защиты: </Col>
                {protection ? (
                <Col>{protection.stage_name || 'Дата не назначена'}</Col>
                ) : (
                <Col>график защит ВКР не создан секретарем ГЭК</Col>
                )}
                </Row>

           
                <Row className='justify-content-center'>
                <Col>Тип ВКР: </Col>
                <Col>{request.type_of_fqw}</Col>
                </Row>

                {request.additional_information && (
                <Row className="justify-content-center">
                    <Col>Дополнительная информация</Col>
                    <Col>{request.additional_information}</Col>
                </Row>
                )}
                


              </div>
            );
          } else {
            return null; // Пропускаем студентов с is_accepted_request, отличным от true
          }
        })}

    </div>
    </>
  )
}

export default SupervisedStudentsCard
