import React, { Component, useState } from 'react';
import styles from './SideMenu.module.css';

const SideMenu = ({ toggleMenu }) => {
    const [isOpen, setIsOpen] = useState(false);
  
    // Добавляем вывод состояния меню в консоль
    console.log("Меню открыто:", isOpen);

    return (
      <>
        <div className={`${styles.sidebar}`} style={{ display: isOpen ? 'block' : 'none' }}>
          <button className="close-btn" onClick={() => { setIsOpen(!isOpen); toggleMenu(); }}>×</button>
          <ul>
            <li><a href='profile_student'>Личный кабинет</a></li>
            <li><a href="view_protection_schedule"> График защит ВКР</a></li>
            <li><a href="#"> Инфо </a></li>
            <li><a href="#"> Приказы </a></li>
          </ul>
        </div>
        {isOpen && <div className={styles.overlay} onClick={toggleMenu}></div>}
      </>
    );
};

export default SideMenu;
