import React, { useState } from 'react';
import styles from './MethodRecommendationsCard.module.css';
import { Tabs, Tab, Row, Col } from 'react-bootstrap';

const MethodRecommendationsCard = () => {
  const [activeTab, setActiveTab] = useState('tab1');

  const handleTabSelect = (tabId) => {
    setActiveTab(tabId);
  };

  return (
    <div className={styles.container}>
      <div className={styles.card}>
        <Row>
          <Col md={11}>
            <Tabs activeKey={activeTab} onSelect={handleTabSelect}>
              <Tab eventKey="tab1" title="Оформление">
                {/* Здесь содержимое для первой вкладки */}
              </Tab>
              <Tab eventKey="tab2" title="Содержание">
                {/* Здесь содержимое для второй вкладки */}
              </Tab>
              <Tab eventKey="tab3" title="Графическая часть">
                {/* Здесь содержимое для третьей вкладки */}
              </Tab>
            </Tabs>
          </Col>
          <Col md={1}>
            <div className="tabs-right">
              <button className={styles.tab_btn} onClick={() => handleTabSelect('tab1')}>Оформление</button>
              <button className={styles.tab_btn} onClick={() => handleTabSelect('tab2')}>Содержание</button>
              <button className={styles.tab_btn} onClick={() => handleTabSelect('tab3')}>Графическая часть</button>
            </div>
          </Col>
        </Row>
      </div>
    </div>
  );
};

export default MethodRecommendationsCard;
